import React, {useEffect, useRef, useState} from 'react';
import { Link } from 'react-router-dom';
import Lottie from "lottie-react";
import {useMediaQuery} from '@react-hook/media-query';
import anime from 'animejs/lib/anime.es.js';
import {isMobile} from 'react-device-detect';

import {useViewport} from '../../Hooks/viewportProvider';

import './accroche.scss';

import boutonflechebas from "../../Assets/animations/boutonflechebas.json";

import accrocheIMG1 from '../../Assets/img/accueil/accueilAccroche1.jpg';
import accrocheIMG2 from '../../Assets/img/accueil/accueilAccroche2.jpg';

const SAAccrocheMobile = ({setAccrocheAffiche, accrocheAffiche}) => {

    //Animation du texte 
    useEffect(() => {
        if(!accrocheAffiche){
            anime({
                targets: '.sousLigne .mot',
                translateY: [100,0],
                easing: "easeOutExpo",
                duration: 1500,
                delay: (el, i) => 60 * i
            });
            anime({
                targets: '.accrocheIMG1',
                scale: [0,1],
                duration: 1200,
                delay:500,
                easing: "easeOutExpo",
            })
            anime({
                targets: '.accrocheIMG2',
                scale: [0,1],
                duration: 1200,
                delay:1000,
                easing: "easeOutExpo",
            })
            setAccrocheAffiche(true)

        }
    }, []);

    return(
        <div className="SA_accrocheMobile">


                <span className="ligne ligne_1"><div className="sousLigne"><span className="mot">Une</span> <span className="mot">nouvelle</span> <span className="mot">génération</span></div></span>
                <span className="ligne ligne_2"><div className="sousLigne"><span className="mot">de</span> <span className="mot">studio</span> <span className="mot">photo</span></div> <img src={accrocheIMG1} className="accrocheIMG1" alt="" /></span>
                <span className="ligne ligne_3"><div className="sousLigne"><span className="mot">et</span> <span className="mot">vidéo</span> <span className="mot">tout</span> <span className="mot">équipé</span></div></span>
                <span className="ligne ligne_4"><div className="sousLigne"><span className="mot">pour </span> <span className="mot">l'e-commerce</span> <span className="mot">et</span></div></span>
                <span className="ligne ligne_5"><img src={accrocheIMG2} className="accrocheIMG2" alt="" /> <div className="sousLigne"><span className="mot">la</span> <span className="mot">création</span></div></span>
                <span className="ligne ligne_6"><div className="sousLigne"> <span className="mot">social</span> <span className="mot">media.</span></div></span>
                <span className="ligne ligne_7"><div className="sousLigne"></div></span>
                <span className="ligne ligne_8"><div className="sousLigne"><span className="mot">E-Do</span> <span className="mot">vous</span> <span className="mot">propose</span> <span className="mot">des</span></div></span>
                <span className="ligne ligne_9"><div className="sousLigne"> <span className="mot">outils</span> <span className="mot">de</span> <span className="mot">shootings<span className="copyright">®</span></span></div></span>
                <span className="ligne ligne_10"><div className="sousLigne"> <span className="mot">incomparables.</span></div></span>




        </div>
    )
}



const SAAccrochePC = ({setAccrocheAffiche, accrocheAffiche}) => {

    //Animation du texte 
    useEffect(() => {
        if(!accrocheAffiche){
            anime({
                targets: '.sousLigne .mot',
                translateY: [100,0],
                easing: "easeOutExpo",
                duration: 1500,
                delay: (el, i) => 60 * i
            });

            anime({
                targets: '.accrocheIMG1',
                scale: [0,1],
                duration: 1200,
                delay:500,
                easing: "easeOutExpo",
            })
            anime({
                targets: '.accrocheIMG2',
                scale: [0,1],
                duration: 1200,
                delay:1000,
                easing: "easeOutExpo",
            })
            setAccrocheAffiche(true)
        }
      }, []);

    return(
        <div className="SA_accrochePC">
            <span className="ligne ligne_1"><div className="sousLigne"><span className="mot">Une</span> <span className="mot">nouvelle</span> <span className="mot">génération</span> <span className="mot">de</span> <span className="mot">studio</span> <span className="mot">photo</span> <span className="mot">et</span> <span className="mot">vidéo</span></div></span>
            <span className="ligne ligne_2"><img src={accrocheIMG1} className="accrocheIMG1" alt="" /> <div className="sousLigne"><span className="mot">tout</span> <span className="mot">équipé</span> <span className="mot">pour</span> <span className="mot">l'e-commerce</span> <span className="mot">et</span> <span className="mot">la</span> <span className="mot">création</span></div></span>
            <span className="ligne ligne_3"><div className="sousLigne"><span className="mot">de</span> <span className="mot">contenu</span> <span className="mot">social</span> <span className="mot">media.</span> <span className="mot">E-Do</span> <span className="mot">vous</span> <span className="mot">propose</span>  </div></span>
            <span className="ligne ligne_4"><div className="sousLigne"><span className="mot">des</span> <span className="mot">outils</span> <span className="mot">de</span> <span className="mot">shootings<span className="copyright">®</span></span> <span className="mot">incomparables.</span></div> <img src={accrocheIMG2} className="accrocheIMG2" alt="" /></span>
        </div>

        // Une nouvelle génération de studio photo et vidéo tout équipé pour l’e-commerce et la création de contenu social media. E-Do vous propose des outils de shootings incomparables.
    )
}


{/* <span className="copyright">®</span> */}
// Une nouvelle 
// génération 
// de studio tout 
// équipé pour la 
// photo e-commerce et 
// la création de 
// contenu social media. 
// E-Do vous propose des 
// outils de shootings® 
// incomparables.

const Accroche = ({pagePreload, fullpageApi, AccrocheSlideLeave, AccrocheSlideLeaveAnimationFin, AccrocheSlideLeaveReset, setAccrocheSlideLeaveAnimationFin, setAccrocheSlideLeave, setAccrocheSlideLeaveReset}) => {
    const sectionAccueil = useRef()
    const SA_accrocheMobileBloc = useRef()
    const SA_accrochePCBloc = useRef()
    const SA_LienReserver = useRef()
    const boutonNav = useRef()
    const animBoutonFlecheBas = useRef()

    const [accrocheAffiche, setAccrocheAffiche] = useState(false)


    const matches = useMediaQuery('only screen and (min-width: 768px)')
    const matchesHeight = useMediaQuery('only screen and (min-height: 700px)')

    //Force le rendu après le redimensionnement de l'écran
    const windowSize = useViewport()

    const [windowSizeState, setWindowSizeState] = useState({
        width: windowSize.width,
        height: windowSize.height
    })

    const [boutonNavClick, setBoutonNavClick] = useState(false)



    useEffect(() => {
        let SA_accroche
        if(fullpageApi){
            //Version mobile ou PC
            SA_accroche = matches ? SA_accrochePCBloc : SA_accrocheMobileBloc

            if(SA_accroche.current){
                //FIX PROBLEME MAXIMISE MINIMISE FENETRE
                if(!isMobile){
                    sectionAccueil.current.style.height = matches ? 'calc(100vh - 76px)' : 'calc(100vh - 90px)'
                }
                //Hauteur de la viewbox minimum
                // fullpageApi.reBuild()
                let paddingSectionAccueil = matches ? 100 : 10
                sectionAccueil.current.style.minHeight = SA_accroche.current.clientHeight + SA_LienReserver.current.clientHeight + boutonNav.current.clientHeight + paddingSectionAccueil + 80 + 'px'

                //Margin Top de l'accroche
                let diviseurSA_Accroche = matches ? 2.35 : 3
                let ajustementPetiteHauteur = !matchesHeight && matches ? 20 : 0
                let SA_AccrocheMarginTop = sectionAccueil.current.clientHeight / diviseurSA_Accroche - SA_accroche.current.clientHeight / 2 - ajustementPetiteHauteur
                SA_AccrocheMarginTop = SA_AccrocheMarginTop < 20 ? 20 : SA_AccrocheMarginTop
                SA_accroche.current.style.marginTop = SA_AccrocheMarginTop + 'px'

                //Margin Top du bouton réserver
                let diviseurSA_LienReserver = matches ? 15 : 1.45
                let SA_LienReserverMarginTop = (sectionAccueil.current.clientHeight - SA_accroche.current.clientHeight - SA_AccrocheMarginTop - boutonNav.current.clientHeight - 30) / diviseurSA_LienReserver
                SA_LienReserver.current.style.marginTop = SA_LienReserverMarginTop + 'px'

                //Margin Top du bouton de navigation
                let margeMiniBoutonNav = matches ? 40 : 10
                let boutonNavMarginTop = sectionAccueil.current.clientHeight - SA_accroche.current.clientHeight - SA_AccrocheMarginTop - SA_LienReserverMarginTop - SA_LienReserver.current.clientHeight - boutonNav.current.clientHeight - margeMiniBoutonNav
                boutonNav.current.style.marginTop = boutonNavMarginTop + 'px'
            }
            if(windowSize.width !== windowSizeState.width || windowSize.height !== windowSizeState.height){
                setWindowSizeState({
                    width: windowSize.width,
                    height: windowSize.height
                })
                fullpageApi.reBuild()

            }
            // fullpageApi.reBuild()
        }

    })


    //Animation du bouton de navigation
    useEffect(() => {
        setTimeout(() =>{
            animBoutonFlecheBas.current.play()
        },200)
    }, [])
    useEffect(() => {

        if(animBoutonFlecheBas.current){

            if(AccrocheSlideLeave){
                animBoutonFlecheBas.current.play()
            } 
            //Reset de l'animation
            if(!AccrocheSlideLeaveAnimationFin && !AccrocheSlideLeave && AccrocheSlideLeaveReset){
                setAccrocheSlideLeaveReset(false)
                animBoutonFlecheBas.current.play()
            }

        }
    })

    useEffect(() => {
        //Gestion du click
        if(boutonNavClick){
            setAccrocheSlideLeave(true)
            setBoutonNavClick(false)
            animBoutonFlecheBas.current.play()
            // setAccrocheSlideLeaveAnimationFin(false)
        }
    }, [boutonNavClick])


    //ANIMATION DU LIEN RESERVER UNE SEANCE
    //Animation du texte 
    useEffect(() => {
        if(!accrocheAffiche){
            anime({
                targets: '.SA_LienReserverContainer .SA_LienReserver',
                translateY: [100,0],
                easing: "easeOutExpo",
                duration: 1800,
                loop:false,
                delay: 700
            });
        }
      }, []);

    return (
        <div className="sectionAccueil" ref={sectionAccueil}>

            {
                !matches ?
                    <div ref={SA_accrocheMobileBloc}>
                        <SAAccrocheMobile setAccrocheAffiche={setAccrocheAffiche} accrocheAffiche={accrocheAffiche} />
                    </div>
                :   <div ref={SA_accrochePCBloc} style={{textAlign:'center'}}>
                        <SAAccrochePC setAccrocheAffiche={setAccrocheAffiche} accrocheAffiche={accrocheAffiche} />
                    </div>
            }

            <div className="SA_LienReserverContainer"><Link to="/reservation" className="SA_LienReserver" ref={SA_LienReserver}>( RÉSERVER UNE SESSION SUR NOS MACHINES )</Link></div>

            <div className="boutonAnimation" ref={boutonNav}>
                <Lottie 
                    className="animBoutonFlecheBas"
                    lottieRef={animBoutonFlecheBas} 
                    animationData={boutonflechebas}
                    onClick={() => {

                        setBoutonNavClick(true)

                    }}
                    autoplay={false}
                    onEnterFrame={(event) => {
                        if(event.currentTime > 36 && !AccrocheSlideLeave){
                            animBoutonFlecheBas.current.pause()
                        }
                        //Animation terminé on lance le slide
                        if(event.currentTime >= event.totalTime - 30 && !AccrocheSlideLeaveAnimationFin){
                            animBoutonFlecheBas.current.pause()
                            setAccrocheSlideLeaveAnimationFin(true)
                            fullpageApi.moveSectionDown()
                        }
                    }}
                />

            </div>

        </div>
    )
}

export default Accroche
