import React, { useEffect, useState, useRef } from "react";
import { useLocation } from "react-router-dom";
import Slider from "react-slick";
import { useMediaQuery } from "@react-hook/media-query";
import Image from "react-image-webp";
import { Link } from "react-router-dom";
import anime from "animejs/lib/anime.es.js";
import { Waypoint } from "react-waypoint";
import { Helmet } from "react-helmet";

import AutresServices from "./autresServices.jsx";
import Footer from "../../Components/Layout/Footer/footer";

import "./service.scss";
import { ConsoleView } from "react-device-detect";

const serviceData = [
  {
    titre: "LIVE / PORTÉ",
    sousTitre: "MISE EN SCÈNE",
    paragraphe: [
      "This 8m2 smart studio system allows you to make your photos and videos of live models or scenographies in minutes. In an instant, your productions are self-edited, formatted, turnkey for e-commerce, Instagram, Snapchat or Facebook. Gain in efficiency to be first on your market",
      "Pour un rendu optimal, l’équipe E-Do peut prendre en charge la retouche de vos visuels.",
    ],
    paragrapheCourt:
      "Créez de superbes photos et vidéos de vos modèles en quelques minutes. Le tout avec une seule configuration robotique tout-en-un.",
    numero: "01",
    imgSlide: ["live/1", "live/2", "live/3", "live/4"],
    imgTarif: "machine-live",
    tarifs: ["1 heure : 170 €", "4 heures : 590 €", "8 heures : 1020 €"],
    nomPage: ["LIVE", "PORTÉ / MISE EN SCÈNE"],
    seo: [
      "E-Do Studio – Live – Shoot vêtements portés et grands produits",
      "Notre ultime produit, la Live est un système de studio intelligent vous permettant de réaliser vos photos et vidéos de modèles vivants ou scénographique en quelques minutes. Des vidéos autoédités, obtenez rapidement tous les visuels dont vous avez besoin.",
      "",
    ],
    slug: "service-mise-en-scene-live",
  },

  {
    titre: "HORIZONTAL /",
    sousTitre: "PACKSHOT À PLAT",
    paragraphe: [
      "Réalisez vos prises de vues packshot à plat grâce à notre machine Horizontal. L’Ipad Pro vous permet de configurer la lumière et  la caméra. Horizontal vous garantie la possibilité de styliser et photographier vos produits à plat avec la suppression automatique de l’arrière-plan.",
      "Pour un rendu optimal, l’équipe E-Do peut prendre en charge la retouche de vos visuels.",
    ],
    paragrapheCourt:
      "Controlée par une seule touche. Il suffit d'un styliste ou d'un photographe unique et de son doigt pour faire fonctionner une machine StyleShoots à l'aide de l'interface iPad incluse.",
    numero: "02",
    imgSlide: ["horizontal/1", "horizontal/2", "horizontal/3", "horizontal/4"],
    imgTarif: "machine-horizontal",
    tarifs: ["1 heure : 110 €", "4 heures : 390 €", "8 heures : 650 €"],
    nomPage: ["HORIZONTAL", "PACKSHOT À PLAT"],
    seo: [
      "E-Do Studio - Horizontal - Packshot produit à plat",
      "Choisissez notre service de prise de vue photo et vidéo packshot Horizontal. Sélectionnez votre offre en self service ou accompagné. La location s’effectue à la journée, demi-journée ou à l’heure. ",
      "",
    ],
    slug: "service-packshot-horizontal",
  },

  {
    titre: "VERTICAL / MANNEQUIN",
    sousTitre: "INVISIBLE",
    paragraphe: [
      "Évitez les retouches fastidieuses et réalisez vos prises de vues sur mannequin invisible grâce à notre machine Vertical. L’Ipad Pro vous permet de configurer la lumière et la caméra. Avec Vertical, donnez du volume à vos produits et choississez votre arrière-plan sans vous soucier du détourage.",
      "Pour un rendu optimal, l’équipe E-Do peut prendre en charge la retouche de vos visuels.",
    ],
    paragrapheCourt:
      "Des images de produits parfaites en un clic. Produits sur mannequin invisible, détection automatique du fond et éclairage tactile contrôlé.",
    numero: "03",
    imgSlide: ["vertical/1", "vertical/2", "vertical/3", "vertical/4"],
    imgTarif: "machine-vertical",
    tarifs: ["1 heure : 110 €", "4 heures : 390 €", "8 heures : 650 €"],
    nomPage: ["VERTICAL", "MANNEQUIN INVISIBLE"],
    seo: [
      "E-Do Studio - Vertical - Packshot mannequin invisible",
      "Découvrez notre service de shooting packshot vertical sur mannequin invisible. Choisissez parmi nos offres en self service ou accompagné. La location s’effectue à la journée, demi-journée ou à l’heure.",
      "",
    ],
    slug: "service-mannequin-vertical",
  },

  {
    titre: "ECLIPSE /",
    sousTitre: "ACCESSOIRES",
    paragraphe: [
      "Eclipse vous permet de repousser vos limites créatives. Conçu pour la photo et la vidéo d’accessoires. Créez vos décors et jouez avec la lumière, l’arrière-plan et des reflets. Organisez vos séries de photos ou séquences vidéos, vous obtiendrez vos rendus en quelques secondes.",
      "Pour un rendu optimal, l’équipe E-Do peut prendre en charge la retouche de vos visuels.",
    ],
    paragrapheCourt:
      "Une touche et la magie opère. Une fois les réglages de vos vidéos et vos photos effectués, Eclipse vous aide à les répéter. ",
    numero: "04",
    imgSlide: [
      "eclipse/1",
      "eclipse/2",
      "eclipse/3",
      "eclipse/4",
      "eclipse/5",
      "eclipse/6",
      "eclipse/7",
    ],
    imgTarif: "machine-eclipse",
    tarifs: ["1 heure : 150 €", "4 heures : 530 €", "8 heures : 890 €"],
    nomPage: ["ECLIPSE", "ACCESSOIRES"],
    seo: [
      "E-Do Studio - Eclipse – Packshot Shoot produits / Accessoires",
      "Repoussez les limites, organisez vos séquences vidéos et séries de photos pour Automatiser votre shoot grâce à notre Eclipse.",
      "",
    ],
    slug: "service-accessoires-eclipse",
  },
  {
    titre: "CYCLORAMA",
    sousTitre: "PRODUCTION LIBRE",
    paragraphe: [
      "Notre cyclorama vous permet de mobiliser une équipe de production et de réaliser vos prises de photos et vidéos sur un fond blanc infini. Avec une hauteur de 4,70m et une profondeur de 10m, vous avez champ libre pour tout type de set design.",
    ],
    paragrapheCourt:
      "Organisez votre session photo ou tournage en toute liberté sur notre cyclorama de 6m x 5m sur 4,70m de haut.",
    numero: "05",
    imgSlide: [],
    imgTarif: "machine-cyclorama",
    tarifs: ["Sur devis : contactez nous"],
    nomPage: ["CYCLORAMA", "PRODUCTION LIBRE"],
    seo: [
      "E-Do Studio - Cyclo - Production libre",
      "Besoin d’un bel espace pour shooter photos et vidéos, pub, e-commerce ou bien d’autres ? Notre Cyclorama de six par cinq avec une hauteur de quatre mètres soixante dix saura vous combler.",
      "",
    ],
    slug: "service-production-libre-cyclorama",
  },
];

const Service = ({ setPageLoad }) => {
  const sliderTop = useRef();
  const sliderBot = useRef();

  const [animTarifETAT, setAnimTarifETAT] = useState(false);

  const matches = useMediaQuery("only screen and (min-width: 992px)");

  let location = useLocation();
  const [pageData, setPageData] = useState({
    titre: "",
    sousTitre: "",
    paragraphe: ["", ""],
    paragrapheCourt: "",
    numero: "",
    imgSlide: [],
    imgTarif: "",
    tarifs: [],
    nomPage: [],
    seo: ["", "", ""],
    slug: "",
  });

  //Lien vers la page contact pour la page cyclorama sinon page reservation
  const lienReservation = "/reservation";

  useEffect(() => {
    window.scrollTo(0, 0);
    setPageLoad(true);

    //Charge les bonnes datas par rapport à l'url
    if (location.pathname === "/service-mise-en-scene-live") {
      setPageData(serviceData[0]);
    } else if (location.pathname === "/service-packshot-horizontal") {
      setPageData(serviceData[1]);
    } else if (location.pathname === "/service-mannequin-vertical") {
      setPageData(serviceData[2]);
    } else if (location.pathname === "/service-accessoires-eclipse") {
      setPageData(serviceData[3]);
    }

    //Animation
    anime({
      targets: ".titreCat, .titreServiceTitre h1, .SliderTop",
      translateY: ["100%", 0],
      easing: "easeOutCubic",
      duration: 800,
      delay: 300,
    });

    anime({
      targets: ".sliderTopControl",
      translateY: ["100%", 0],
      easing: "easeOutCubic",
      duration: 800,
      delay: 300,
    });

    anime({
      targets: ".paragraphes",
      translateY: ["100%", 0],
      easing: "easeOutCubic",
      duration: 800,
      delay: 550,
    });

    anime({
      targets: ".grosNombre",
      translateY: ["100%", 0],
      easing: "easeOutCubic",
      duration: 800,
      delay: 750,
    });
  }, []);

  const sliderTopSettings = {
    dots: false,
    arrows: false,
    infinite: true,
    speed: 200,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 2000,
  };

  const sliderTopNav = (direction) => {
    if (direction === "prec") {
      sliderTop.current.slickPrev();
    }
    if (direction === "suiv") {
      sliderTop.current.slickNext();
    }
  };

  const animTarifs = () => {
    // console.log('anim', animTarifETAT)

    if (!animTarifETAT) {
      //
      console.log("anim");
      setAnimTarifETAT(true);
      // document.querySelector('.PS_TarifsIMG').style.opacity = 1

      anime({
        targets: ".PS_TarifsIMG",
        translateY: ["40%", 0],
        scale: [0.7, 1],
        opacity: [0, 1],
        delay: 100,
        duration: 900,
        easing: "easeOutCubic",
      });

      anime({
        targets: ".PS_TarifsTextUnique h1, .titreTarifs",
        translateY: ["120%", 0],
        delay: (el, i) => 80 * i + 200,
        duration: 900,
        easing: "easeOutCubic",
        complete: () => {},
      });

      anime({
        targets: ".tarifAnim",
        translateY: ["40", 0],
        delay: (el, i) => 80 * i + 200,
        duration: 900,
        easing: "easeOutCubic",
        complete: () => {},
      });

      anime({
        targets: ".PS_TarifsTextUnique .tarif",
        translateY: ["110%", 0],
        delay: (el, i) => 80 * i + 200,
        duration: 900,
        easing: "easeOutCubic",
      });

      anime({
        targets: ".PS_TarifsTextUniqueDetail svg",
        translateX: ["-110%", 0],
        delay: (el, i) => 80 * i + 200,
        duration: 900,
        easing: "easeOutCubic",
      });

      anime({
        targets: ".boutonReserver",
        scale: [0, 1],
        duration: 500,
        easing: "easeOutCubic",
        delay: 500,
      });
    }
  };

  const animBoutonRond = () => {
    anime({
      targets: ".boutonReserver",
      // translateY: [550,0],
      scale: [1, 1.2],
      easing: "easeOutExpo",
      duration: 600,
    });
  };

  const animBoutonRondLeave = () => {
    anime({
      targets: ".boutonReserver",
      // translateY: [550,0],
      scale: 1,
      easing: "easeOutExpo",
      duration: 600,
    });
  };

  return (
    <>
      <Helmet defer={false}>
        <meta charSet="utf-8" />
        <title>{pageData.seo[0]}</title>
        <meta name="description" content={pageData.seo[1]} />

        {/* <link rel="canonical" href="http://mysite.com/example" /> */}
      </Helmet>

      <div className="pageService">
        <div className="PS_Presentation">
          <div className="PS_PresentationWrapper">
            <div className="animationWrapper" style={{ height: "40px" }}>
              <div className="titreCat">NOS SERVICES</div>
            </div>

            <div className="titreService">
              <div className="titreServiceTitre">
                <div className="animationWrapperTitre">
                  <h1>
                    {pageData.titre}
                    <br />
                    {pageData.sousTitre}
                  </h1>
                </div>
                {!matches ? (
                  <div className="animationWrapper">
                    <h2>{pageData.numero}</h2>
                  </div>
                ) : (
                  ""
                )}
              </div>

              <div className="animationWrapper">
                <div className="paragraphes">
                  {pageData.paragraphe.map((paragrapheUnique, index) => {
                    return <p key={index}>{paragrapheUnique}</p>;
                  })}
                </div>
              </div>

              {matches ? (
                <div className="animationWrapper">
                  <div className="grosNombre">{pageData.numero}</div>
                </div>
              ) : (
                ""
              )}
            </div>

            <div className="sliderTop">
              {location.pathname !== "/service-production-libre-cyclorama" ? (
                <div className="animationWrapper">
                  <Slider
                    {...sliderTopSettings}
                    ref={sliderTop}
                    className="SliderTop"
                  >
                    {pageData.imgSlide.map((imgSlide, index) => {
                      return (
                        <div key={index} className="sliderTopUnique">
                          <img
                            className="sliderTopUnique_IMG"
                            src={
                              process.env.PUBLIC_URL +
                              "/img/services/" +
                              imgSlide +
                              ".jpg"
                            }
                          />
                        </div>
                      );
                    })}
                  </Slider>

                  <div className="sliderTopControlsBox">
                    <div className="animationWrapper">
                      <div
                        className="sliderTopControlsLeft sliderTopControl"
                        onClick={() => {
                          sliderTopNav("prec");
                        }}
                      >
                        <svg
                          xmlns="http://www.w3.org/2000/svg"
                          viewBox="0 0 7.06 12.71"
                        >
                          <path
                            fill="none"
                            stroke="#000"
                            d="M6.71.35l-6 6 6 6"
                          />
                        </svg>
                      </div>
                    </div>
                    <div className="animationWrapper">
                      <div
                        className="sliderTopControlsRight sliderTopControl"
                        onClick={() => {
                          sliderTopNav("suiv");
                        }}
                      >
                        <svg
                          xmlns="http://www.w3.org/2000/svg"
                          viewBox="0 0 7.06 12.71"
                        >
                          <path
                            fill="none"
                            stroke="#000"
                            d="M6.71.35l-6 6 6 6"
                          />
                        </svg>
                      </div>
                    </div>
                  </div>
                </div>
              ) : (
                ""
              )}
            </div>
          </div>
        </div>

        <div className="PS_Tarifs">
          <Waypoint
            onEnter={animTarifs}
            // onLeave={this._handleWaypointLeave}
          />
          <div className="PS_TarifsText">
            <div>
              <div className="PS_TarifsTextUnique">
                <div className="animationWrapperTitre2">
                  <h1>HORAIRE</h1>
                </div>
                <div className="PS_TarifsTextUniqueDetail">
                  <div className="animationWrapper">
                    <svg
                      width="31"
                      height="13"
                      xmlns="http://www.w3.org/2000/svg"
                    >
                      <path
                        stroke="#000"
                        fill="none"
                        fillRule="evenodd"
                        d="M.9 7.1l29.2-.5M24 .6l6 6-6 6"
                      />
                    </svg>
                  </div>
                  <div className="animationWrapper">
                    <div className="tarif">{pageData.tarifs[0]} ht</div>
                  </div>
                </div>
              </div>

              <div className="PS_TarifsTextUnique">
                <div className="animationWrapperTitre2">
                  <h1>DEMI-JOURNÉE</h1>
                </div>
                <div className="PS_TarifsTextUniqueDetail">
                  <div className="animationWrapper">
                    <svg
                      width="31"
                      height="13"
                      xmlns="http://www.w3.org/2000/svg"
                    >
                      <path
                        stroke="#000"
                        fill="none"
                        fillRule="evenodd"
                        d="M.9 7.1l29.2-.5M24 .6l6 6-6 6"
                      />
                    </svg>
                  </div>
                  <div className="animationWrapper">
                    <div className="tarif">{pageData.tarifs[1]} ht</div>
                  </div>
                </div>
              </div>

              <div className="PS_TarifsTextUnique">
                <div className="animationWrapperTitre2">
                  <h1>JOURNÉE</h1>
                </div>
                <div className="PS_TarifsTextUniqueDetail">
                  <div className="animationWrapper">
                    <svg
                      width="31"
                      height="13"
                      xmlns="http://www.w3.org/2000/svg"
                    >
                      <path
                        stroke="#000"
                        fill="none"
                        fillRule="evenodd"
                        d="M.9 7.1l29.2-.5M24 .6l6 6-6 6"
                      />
                    </svg>
                  </div>
                  <div className="animationWrapper">
                    <div className="tarif">{pageData.tarifs[2]} ht</div>
                  </div>
                </div>
              </div>
            </div>

            <div className="boutonReserverBlock">
              <Link
                to={lienReservation}
                className="boutonReserver"
                onMouseEnter={animBoutonRond}
                onMouseLeave={animBoutonRondLeave}
              >
                Réserver
              </Link>
            </div>

            <div className="titreTarifs">
              <div className="animationWrapper">
                <div className="tarifAnim">TARIFS</div>
                <div className="feesBox">*Weekend fees 25%.</div>
              </div>
            </div>
          </div>
          <div className="PS_TarifsIMGBox">
            <img
              className="PS_TarifsIMG"
              src={
                process.env.PUBLIC_URL +
                "/img/menuservices/" +
                pageData.imgTarif +
                ".jpg"
              }
            />
          </div>

          {matches ? (
            <div className="titreSection">
              <span className="titreSectionFonce">{pageData.nomPage[0]} </span>
              <span className="titreSectionClair">{pageData.nomPage[1]}</span>
            </div>
          ) : (
            ""
          )}
        </div>

        <div className="PS_AutresServices">
          <div className="PS_AutresServicesWrapper">
            <div className="PS_TitreSection">AUTRES SERVICES</div>
            <AutresServices location={location.pathname} data={serviceData} />
          </div>
        </div>
      </div>
      <Footer AnimationBloc7={true} colorTheme="white" />
    </>
  );
};

export default Service;
