import i18next from "i18next";
import { initReactI18next } from "react-i18next";

// import your translation files

import menuFr from "./translation/en/menu.json";

import argumentFr from "./translation/en/argument.json";

// initialize the i18next library
i18next.use(initReactI18next).init({
  resources: {
    fr: {
      menu: menuFr,
      argument: argumentFr,
    },
  },
  lng: "fr",
  fallbackLng: "en",
  interpolation: {
    escapeValue: false,
  },
});

const saveLanguage = () => {
  i18n.saveLanguage();
};

// load the saved language if it exists
const loadLanguage = () => {
  i18n.loadLanguage();
};

export default i18next;
