import { useTexture } from "@react-three/drei";
import { memo, useRef } from "react";
import { PlaneGeometry, Mesh, MeshBasicMaterial } from "three";
import image from "./test.png";

export const Photo = memo(({ x, y }) => {
  const texture = useTexture(image);
  const meshRef = useRef();

  // Calculer les dimensions du plan en fonction de la taille de l'image
  const imageWidth = texture.image.width;
  const imageHeight = texture.image.height;
  const aspectRatio = imageWidth / imageHeight;

  // Utiliser PlaneGeometry avec une largeur unitaire (1) et une hauteur calculée
  const geometry = new PlaneGeometry(1, 1 / aspectRatio);

  // Mettre à jour le scale du mesh pour remplir l'espace disponible
  const updateScale = () => {
    const parentWidth = meshRef.current.parent.scale.x;
    const parentHeight = meshRef.current.parent.scale.y;
    const scaleX = parentWidth / imageWidth;
    const scaleY = parentHeight / imageHeight;
    meshRef.current.scale.set(scaleX, scaleY, 1);
  };

  // Appeler updateScale lorsque la taille du parent change
  useLayoutEffect(() => {
    updateScale();
  }, [meshRef.current.parent.scale.x, meshRef.current.parent.scale.y]);

  return (
    <mesh position={[x, y, 0]} geometry={geometry} ref={meshRef}>
      <meshBasicMaterial map={texture} />
    </mesh>
  );
});
