import { useState, useRef, useEffect } from "react";
import { BsChevronLeft, BsChevronRight } from "react-icons/bs";
import { useMediaQuery } from "@react-hook/media-query";

import Slider from "react-slick";

// Traduction
import i18next, { t } from "i18next";
import { useTranslation } from "react-i18next";

// Ghost
import image1 from "./img/brown_34_Front.png";
import image2 from "./img/brown_34_inside.jpg";
import image3 from "./img/Lola_Front.png";
import image4 from "./img/Lola_Front2.jpg";
import image5 from "./img/suitcomplete_Front.png";
import image6 from "./img/suit_jacket_Inside.jpg";

// Accessoires / Chaussures
import image7 from "./img/bottes_rose_before.jpg";
import image8 from "./img/bottes_rose_after.png";
import image9 from "./img/alison_semah_casquette.jpg";
import image10 from "./img/alison_semah_casquette_retouche.jpg";
import image11 from "./img/moon_view_cagoule_noire.jpg";
import image12 from "./img/moon_view_cagoule_noire_retouche.png";
import image13 from "./img/nfl_casque_blanc.jpg";
import image14 from "./img/nfl_casque_retouche.jpg";
import image15 from "./img/gianbatista_shoes.jpg";
import image16 from "./img/alison_SEMAH_chaussures_retouche.jpg";

//Bijoux
import image17 from "./img/alison_semah_boucle.jpg";
import image18 from "./img/alison_semah_boucle_retouche.jpg";
import image19 from "./img/millefiora-isola-bella-ring-pink-gold.jpg";
import image20 from "./img/millefiora-isola-bella-ring-pink-gold_retouche.jpg";
import image21 from "./img/millefiora-isola.jpg";
import image22 from "./img/millefiora-isola_retouche.jpg";
import image23 from "./img/vaillant_gold_ring.jpg";
import image24 from "./img/vaillant_gold_ring_retouche.jpg";

// On model
import image25 from "./img/alison_semah.jpg";
import image26 from "./img/alison_semah_retouche.jpg";
import image27 from "./img/moka_jacket.jpg";
import image28 from "./img/moka_retouche.jpg";

// Lunettes
import image29 from "./img/alison_semah_lunettes.jpg";
import image30 from "./img/alison_semah_lunettes_retouche.jpg";
import image37 from "./img/port_tanger_lunettes_noires.jpg";
import image38 from "./img/port_tanger_lunettes_noires_retouche.jpg";
import image39 from "./img/port_tanger_lunettes_marron.jpg";
import image40 from "./img/port_tanger_lunettes_marron_retouche.jpg";

// À plat
import image31 from "./img/attire_the_studio_cropped_blazer_white.jpg";
import image32 from "./img/attire_the_studio_cropped_blazer_white_retouche.jpg";
import image33 from "./img/inoui.png";
import image34 from "./img/inoui_retouche.png";
import image35 from "./img/t-shirt_creme_hast.jpg";
import image36 from "./img/t-shirt_creme_hast_retouche.jpg";

const RetouchesCarousel = ({ selectedCat, setSelectedCat }) => {
  const { t, i18n } = useTranslation("retouches");
  const [hover, setIsHover] = useState(false);
  const [activeIndex, setActiveIndex] = useState(0); // nouvel état pour suivre l'indice de l'élément actif

  const matches = useMediaQuery("only screen and (min-width: 1024px)");

  let images = [];

  if (selectedCat === "ghost") {
    images = [
      [image1, image2],
      [image3, image4],
      [image5, image6],
    ];
  } else if (selectedCat === "access") {
    images = [
      [image7, image8],
      [image9, image10],
      [image11, image12],
      [image13, image14],
      [image15, image16],
    ];
  } else if (selectedCat === "bijoux") {
    images = [
      [image17, image18],
      [image19, image20],
      [image21, image22],
      [image23, image24],
    ];
  } else if (selectedCat === "onModel") {
    images = [
      [image25, image26],
      [image27, image28],
    ];
  } else if (selectedCat === "lunettes") {
    images = [
      [image29, image30],
      [image37, image38],
      [image39, image40],
    ];
  } else if (selectedCat === "plat") {
    images = [
      [image31, image32],
      [image33, image34],
      [image35, image36],
    ];
  }

  const handleMouseEnter = (image) => {
    setIsHover(true);
  };

  const handleMouseLeave = () => {
    setIsHover(null);
  };

  // const handleLeftClick = () => {
  //   const lastIndex = images.length - 1;
  //   const index = activeIndex === 0 ? lastIndex : activeIndex - 1;
  //   setActiveIndex(index);

  //   const currentImage = containerRef.current.children[index];
  //   const previousIndex = index === lastIndex ? 0 : index + 1;
  //   const previousImage = containerRef.current.children[previousIndex];

  //   // Animation between images
  //   // previousImage.style.transform = "translateX(0%) scale(1)"; // initial state of previous image
  //   // currentImage.style.transform = "translateX(-100%) scale(1.2)"; // animate current image

  //   // setTimeout(() => {
  //   //   previousImage.style.transition = "transform 1s"; // add transition to previous image
  //   //   previousImage.style.transform = "translateX(100%) scale(0.8)"; // animate previous image
  //   //   currentImage.style.transition = "transform 1s"; // add transition to current image
  //   //   currentImage.style.transform = "translateX(0%) scale(1)"; // animate current image back to initial state
  //   // }, 0);

  //   // // reset the position of the non-hovered image after a brief delay
  //   // setTimeout(() => {
  //   //   const hoverImage = containerRef.current.querySelector(":hover");
  //   //   if (hoverImage && hoverImage !== previousImage) {
  //   //     hoverImage.addEventListener(
  //   //       "transitionend",
  //   //       () => {
  //   //         hoverImage.style.transition = "";
  //   //         hoverImage.style.transform = "translateX(0%) scale(1)";
  //   //       },
  //   //       { once: true }
  //   //     );
  //   //   }
  //   // }, 1000);
  // };

  const sliderTop = useRef();
  const sliderBot = useRef();

  const [intervalId, setIntervalId] = useState(null);

  // const handleRightClick = () => {
  //   if (!containerRef.current) return;

  //   // Image suivante
  //   const lastIndex = images.length - 1;
  //   const index = activeIndex === lastIndex ? 0 : activeIndex + 1;
  //   setActiveIndex(index);

  //   const currentImage = containerRef.current.children[index];
  //   const previousIndex = index === 0 ? lastIndex : index - 1;
  //   const previousImage = containerRef.current.children[previousIndex];
  // };

  // useEffect(() => {
  //   const newIntervalId = setInterval(() => {
  //     handleRightClick();
  //   }, 4000);
  //   setIntervalId(newIntervalId);

  //   // Nettoyer l'intervalle lorsqu'il n'est plus nécessaire
  //   return () => {
  //     clearInterval(intervalId);
  //   };
  // }, [activeIndex]);
  // ...

  const containerRef = useRef(null);

  const handleKeyDown = (event) => {
    if (event.key === "ArrowLeft") {
      handleLeftClick();
    } else if (event.key === "ArrowRight") {
      handleRightClick();
    }
  };

  useEffect(() => {
    setActiveIndex(0);
    containerRef.current.focus();
  }, [selectedCat]);

  const sliderTopSettings = {
    dots: false,
    arrows: false,
    infinite: true,
    speed: 200,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 2000,
  };

  const sliderTopNav = (direction) => {
    if (direction === "prec") {
      sliderTop.current.slickPrev();
    }
    if (direction === "suiv") {
      sliderTop.current.slickNext();
    }
  };

  return (
    <>
      {matches ? (
        <>
          <div className="carousel" onKeyDown={handleKeyDown} tabIndex={0}>
            <div className="controllers">
              <div
                className="control leftControl"
                onClick={() => {
                  sliderTopNav("prec");
                }}
              >
                <BsChevronLeft />
              </div>
              <div
                className="control rightControl"
                onClick={() => {
                  sliderTopNav("suiv");
                }}
              >
                <BsChevronRight />
              </div>
            </div>
            {selectedCat === "ghost" && (
              <>
                <Slider
                  {...sliderTopSettings}
                  ref={sliderTop}
                  className="SliderTop"
                >
                  {images.map((imageSet, index) => (
                    <div
                      // className={`imagesContainer ${
                      //   index === activeIndex ? "active" : "noActive"
                      // }`}
                      className="sliderTopUnique"
                      key={index}
                      ref={containerRef}
                      style={{
                        display: "flex",
                        justifyContent: "center",
                        flexDirection: "",
                      }}
                    >
                      <img
                        className={`sliderTopUnique ${
                          hover === true ? "noActive" : ""
                        } image-transition`}
                        src={imageSet[0]}
                        alt="test"
                        onMouseEnter={handleMouseEnter}
                        onMouseLeave={handleMouseLeave}
                        style={{ width: "50%" }}
                      />
                      <img
                        className={`sliderTopUnique ${
                          hover === true ? "hoverActive" : ""
                        } image-transition`}
                        src={imageSet[1]}
                        alt="test"
                        onMouseEnter={handleMouseEnter}
                        onMouseLeave={handleMouseLeave}
                        style={{ width: "50%" }}
                      />
                      <div>
                        {hover === true ? (
                          <p style={{ paddingTop: "10px" }}>{t("After")}</p>
                        ) : (
                          <p style={{ paddingTop: "10px" }}>{t("Before")}</p>
                        )}
                      </div>
                    </div>
                  ))}
                </Slider>
              </>
            )}
            {selectedCat === "access" && (
              <>
                {images.map((imageSet, index) => (
                  <div
                    className={`imagesContainer ${
                      index === activeIndex ? "active" : "noActive"
                    }`}
                    key={index}
                    ref={containerRef}
                  >
                    <img
                      className={`imageRetouche ${
                        hover === true ? "noActive" : ""
                      } image-transition`}
                      src={imageSet[0]}
                      alt="test"
                      onMouseEnter={handleMouseEnter}
                      onMouseLeave={handleMouseLeave}
                    />
                    <img
                      className={`hoverImage ${
                        hover === true ? "hoverActive" : ""
                      } image-transition`}
                      src={imageSet[1]}
                      alt="test"
                      onMouseEnter={handleMouseEnter}
                      onMouseLeave={handleMouseLeave}
                    />
                    {hover === true ? (
                      <p style={{ paddingTop: "10px" }}>{t("After")}</p>
                    ) : (
                      <p style={{ paddingTop: "10px" }}>{t("Before")}</p>
                    )}
                  </div>
                ))}
              </>
            )}
            {selectedCat === "bijoux" && (
              <>
                {images.map((imageSet, index) => (
                  <div
                    className={`imagesContainer ${
                      index === activeIndex ? "active" : "noActive"
                    }`}
                    key={index}
                    ref={containerRef}
                  >
                    <img
                      className={`imageRetouche ${
                        hover === true ? "noActive" : ""
                      } image-transition`}
                      src={imageSet[0]}
                      alt="test"
                      onMouseEnter={handleMouseEnter}
                      onMouseLeave={handleMouseLeave}
                    />
                    <img
                      className={`hoverImage ${
                        hover === true ? "hoverActive" : ""
                      } image-transition`}
                      src={imageSet[1]}
                      alt="test"
                      onMouseEnter={handleMouseEnter}
                      onMouseLeave={handleMouseLeave}
                    />
                    {hover === true ? (
                      <p style={{ paddingTop: "10px" }}>{t("After")}</p>
                    ) : (
                      <p style={{ paddingTop: "10px" }}>{t("Before")}</p>
                    )}
                  </div>
                ))}
              </>
            )}
            {selectedCat === "onModel" && (
              <>
                {images.map((imageSet, index) => (
                  <div
                    className={`imagesContainer ${
                      index === activeIndex ? "active" : "noActive"
                    }`}
                    key={index}
                    ref={containerRef}
                  >
                    <img
                      className={`imageRetouche ${
                        hover === true ? "noActive" : ""
                      } image-transition`}
                      src={imageSet[0]}
                      alt="test"
                      onMouseEnter={handleMouseEnter}
                      onMouseLeave={handleMouseLeave}
                    />
                    <img
                      className={`hoverImage ${
                        hover === true ? "hoverActive" : ""
                      } image-transition`}
                      src={imageSet[1]}
                      alt="test"
                      onMouseEnter={handleMouseEnter}
                      onMouseLeave={handleMouseLeave}
                    />
                    {hover === true ? (
                      <p style={{ paddingTop: "10px" }}>{t("After")}</p>
                    ) : (
                      <p style={{ paddingTop: "10px" }}>{t("Before")}</p>
                    )}
                  </div>
                ))}
              </>
            )}
            {selectedCat === "lunettes" && (
              <>
                {images.map((imageSet, index) => (
                  <div
                    className={`imagesContainer ${
                      index === activeIndex ? "active" : "noActive"
                    }`}
                    key={index}
                    ref={containerRef}
                  >
                    <img
                      className={`imageRetouche ${
                        hover === true ? "noActive" : ""
                      } image-transition`}
                      src={imageSet[0]}
                      alt="test"
                      onMouseEnter={handleMouseEnter}
                      onMouseLeave={handleMouseLeave}
                    />
                    <img
                      className={`hoverImage ${
                        hover === true ? "hoverActive" : ""
                      } image-transition`}
                      src={imageSet[1]}
                      alt="test"
                      onMouseEnter={handleMouseEnter}
                      onMouseLeave={handleMouseLeave}
                    />
                    {hover === true ? (
                      <p style={{ paddingTop: "10px" }}>{t("After")}</p>
                    ) : (
                      <p style={{ paddingTop: "10px" }}>{t("Before")}</p>
                    )}
                  </div>
                ))}
              </>
            )}
            {selectedCat === "plat" && (
              <>
