import { useCallback, useEffect, useRef, useState } from "react";
import { useLocation } from "react-router-dom";
import { Link } from "react-router-dom";
import { useMediaQuery } from "@react-hook/media-query";
import { Waypoint } from "react-waypoint";
import ScrollContainer from "react-indiana-drag-scroll";
import { Helmet } from "react-helmet";
import Lottie from "lottie-react";

import { useTranslation } from "react-i18next";

const GalerieMenu = ({ setPageLoad, selectedLink, setSelectedLink }) => {
  const titrePageGalerie = useRef();

  const matches = useMediaQuery("only screen and (min-width: 1200px)");

  useEffect(() => {
    window.scrollTo(0, 0);
    setPageLoad(true);

    titrePageGalerie.current.style.transform = "translateY(0%)";
  }, []);

  // const location = useLocation();
  // const { selectedLink = "eclipse" } = location.state || {};

  // Active link
  const [active, setActive] = useState("eclipse");

  const { t, i18n } = useTranslation("gallery");

  console.log(selectedLink);

  return (
    <>
      <Helmet defer={false}>
        <meta charSet="utf-8" />
        <title>E-Do Studio - Galerie shooting Horizontal</title>
        <meta
          name="description"
          content="Explorez les possibilités de productions de contenus photos offertes par notre machine Horizontal."
        />

        {/* <link rel="canonical" href="http://mysite.com/example" /> */}
      </Helmet>
      <div className="pageGalerie">
        <div className="titreAnimationWrapper">
          <h1 className="titrePageGalerie" ref={titrePageGalerie}>
            ECLIPSE
            {/* <span>
              {selectedLink === "Garments" || selectedLink === "Books"
                ? `HORIZONTAL / ${t(selectedLink)}`
                : "HORIZONTAL"}
            </span> */}
          </h1>
          <ul>
            <Link to="/galerie">
              <li>all</li>
            </Link>
            <Link to="/galerie-horizontal">
              <li>Horizontal</li>
              <ul className="sub-category">
                <Link
                  to={{
                    pathname: "/galerie-horizontal",
                    state: { selectedLink: "Garments" },
                  }}
                >
                  <li className={selectedLink === "Garments" ? "active" : ""}>
                    — {t("Garments")}
                  </li>
                </Link>
                <Link
                  to={{
                    pathname: "/galerie-horizontal",
                    state: { selectedLink: "Books" },
                  }}
                >
                  <li className={selectedLink === "Books" ? "active" : ""}>
                    — {t("Books")}
                  </li>
                </Link>
              </ul>
            </Link>
            <Link to="/galerie-vertical">
              <li>Vertical</li>
              <ul className="sub-category">
                <li>— {t("Ghost packshots")}</li>
                <li>— Piqués</li>
              </ul>
            </Link>
            <Link to="/galerie-live">
              <li>Live</li>
              <ul className="sub-category">
                <li>— {t("Garments")}</li>
                <li>— {t("Furnitures")}</li>
              </ul>
            </Link>

            <li
              onClick={() => setSelectedLink("eclipse")}
              className={selectedLink === "eclipse" ? "active" : ""}
            >
              Eclipse
            </li>
            <ul className="sub-category">
              <Link
                to={{
                  pathname: "/galerie-eclipse",
                  state: { selectedLink: "eclipseAccess" },
                }}
              >
                <li>— {t("Accessories")}</li>
              </Link>
              <Link
                to={{
                  pathname: "/galerie-eclipse",
                  state: { selectedLink: "eclipseShoes" },
                }}
              >
                <li className={selectedLink === "eclipseShoes" ? "active" : ""}>
                  — {t("Shoes")}
                </li>
              </Link>
              <Link
                to={{
                  pathname: "/galerie-eclipse",
                  state: { selectedLink: "eclipseBags" },
                }}
              >
                <li className={selectedLink === "eclipseBags" ? "active" : ""}>
                  — {t("Bags")}
                </li>
              </Link>
              <Link
                to={{
                  pathname: "/galerie-eclipse",
                  state: { selectedLink: "eclipseGlasses" },
                }}
              >
                <li
                  className={selectedLink === "eclipseGlasses" ? "active" : ""}
                >
                  — {t("Glasses")}
                </li>
              </Link>
              <Link
                to={{
                  pathname: "/galerie-eclipse",
                  state: { selectedLink: "eclipseCosmetics" },
                }}
              >
                <li
                  className={
                    selectedLink === "eclipseCosmetics" ? "active" : ""
                  }
                >
                  — {t("Cosmetics")}
                </li>
              </Link>
              <Link
                to={{
                  pathname: "/galerie-eclipse",
                  state: { selectedLink: "eclipseBooks" },
                }}
              >
                <li className={selectedLink === "eclipseBooks" ? "active" : ""}>
                  — {t("Books")}
                </li>
              </Link>
              <Link
                to={{
                  pathname: "/galerie-eclipse",
                  state: { selectedLink: "eclipseJewelry" },
                }}
              >
                <li
                  className={selectedLink === "eclipseJewelry" ? "active" : ""}
                >
                  — {t("Jewelry")}
                </li>
              </Link>
              <Link
                to={{
                  pathname: "/galerie-eclipse",
                  state: { selectedLink: "eclipseFood" },
                }}
              >
                <li className={selectedLink === "eclipseFood" ? "active" : ""}>
                  — {t("Food")}
                </li>
              </Link>
            </ul>

            <li>360 Interactive</li>
            <ul className="sub-category">
              <li>— {t("Garments")}</li>
              <li>— {t("Shoes")}</li>
              <li>— {t("Bags")}</li>
              <li>— {t("Accessories")}</li>
              <li>— {t("Food")}</li>
            </ul>
          </ul>
        </div>
        {/* {!matches ? (
          <GalerieMobile selectedLink={selectedLink} />
        ) : (
          <GalerieDesktop selectedLink={selectedLink} />
        )} */}
      </div>
      {/* <Footer AnimationBloc7={true} /> */}
    </>
  );
};

export default GalerieMenu;
