import React, { useState, useRef, useEffect } from "react";
import { BsChevronLeft, BsChevronRight } from "react-icons/bs";

import image1 from "./img/brown_34_Front.png";
import image2 from "./img/brown_34_inside.jpg";
import image3 from "./img/Lola_Front.png";
import image4 from "./img/Lola_Front2.jpg";
import image5 from "./img/suitcomplete_Front.png";
import image6 from "./img/suit_jacket_Inside.jpg";
import image7 from "./img/bottes_rose_before.jpg";
import image8 from "./img/bottes_rose_after.png";

const RetouchesCarousel = ({ selectedCat, setSelectedCat }) => {
  const [hover, setIsHover] = useState(false);
  const [isLoading, setIsLoading] = useState(true);
  const [activeIndex, setActiveIndex] = useState(0); // nouvel état pour suivre l'indice de l'élément actif

  let images = [];

  if (selectedCat === "onModel") {
    images = [
      [image2, image1],
      [image4, image3],
      [image6, image5],
    ];
  } else if (selectedCat === "access") {
    images = [
      [image8, image7],
      // [image9, image10],
      // [image11, image12],
    ];
  } else {
    // fallback to default images
    images = [
      [image1, image2],
      [image3, image4],
      [image5, image6],
    ];
  }

  const handleImageLoad = () => {
    setIsLoading(false);
  };

  const handleMouseEnter = (image) => {
    setIsHover(true);
    setIsLoading(true);
  };

  const handleMouseLeave = () => {
    setIsHover(null);
    setIsLoading(true);
  };

  const handleLeftClick = () => {
    const previousIndex =
      activeIndex - 1 < 0 ? images.length - 1 : activeIndex - 1;
    const previousImage = containerRef.current.querySelector(
      `.images:nth-child(${previousIndex + 1}) .imageRetouche`
    );
    previousImage.classList.add("fade-out");

    setActiveIndex(previousIndex);
  };

  const handleRightClick = () => {
    const nextIndex = activeIndex + 1 >= images.length ? 0 : activeIndex + 1;
    const nextImage = containerRef.current.querySelector(
      `.images:nth-child(${nextIndex + 1}) .imageRetouche`
    );
    nextImage.classList.add("fade-out");

    setActiveIndex(nextIndex);
  };

  useEffect(() => {
    const handleTransitionEnd = (event) => {
      if (event.propertyName === "opacity") {
        const image = event.target;
        image.classList.remove("fade-out");
      }
    };

    const images = containerRef.current.querySelectorAll(".imageRetouche");
    images.forEach((image) =>
      image.addEventListener("transitionend", handleTransitionEnd)
    );

    return () => {
      images.forEach((image) =>
        image.removeEventListener("transitionend", handleTransitionEnd)
      );
    };
  }, []);

  const containerRef = useRef(null);

  const handleKeyDown = (event) => {
    console.log(event.key); // Ajout de console.log
    if (event.key === "ArrowLeft") {
      handleLeftClick();
    } else if (event.key === "ArrowRight") {
      handleRightClick();
    }
  };

  useEffect(() => {
    setActiveIndex(0);
    containerRef.current.focus();
  }, [selectedCat]);

  return (
    <>
      <div className="controllers">
        <div className="control leftControl" onClick={handleLeftClick}>
          <BsChevronLeft />
        </div>
        <div className="control rightControl" onClick={handleRightClick}>
          <BsChevronRight />
        </div>
      </div>
      <div
        className="carousel"
        onLoad={handleImageLoad}
        onMouseEnter={handleMouseEnter}
        onMouseLeave={handleMouseLeave}
        ref={containerRef}
        onKeyDown={handleKeyDown}
        tabIndex={0}
        style={{ transition: "opacity 0.5s ease-out" }}
      >
        {selectedCat === "onModel" && (
          <>
            {images.map((imageSet, index) => (
              <div
                className={`images ${
                  index === activeIndex ? "active" : "noActive"
                }`}
                key={index}
                style={{
                  opacity: index === activeIndex ? 1 : 0,
                  visibility: index === activeIndex ? "visible" : "hidden",
                }}
              >
                <img
                  className={`imageRetouche ${
                    hover === true ? "noActive" : ""
                  } ${
                    index === activeIndex ? "image-active" : "image-inactive"
                  }`}
                  src={imageSet[0]}
                  alt="test"
                />
                <img
                  className={`hoverImage ${
                    hover === true ? "hoverActive" : ""
                  } ${
                    index === activeIndex ? "image-inactive" : "image-active"
                  }`}
                  src={imageSet[1]}
                  alt="test"
                />
              </div>
            ))}
          </>
        )}
        {selectedCat === "access" && (
          <>
            {images.map((imageSet, index) => (
              <div
                className={`images ${
                  index === activeIndex ? "active" : "noActive"
                }`}
                key={index}
                style={{
                  opacity: index === activeIndex ? 1 : 0,
                  visibility: index === activeIndex ? "visible" : "hidden",
                }}
              >
                <img
                  className={`imageRetouche ${
                    hover === true ? "noActive" : ""
                  } ${
                    index === activeIndex ? "image-active" : "image-inactive"
                  }`}
                  src={imageSet[0]}
                  alt="test"
                />
                <img
                  className={`hoverImage ${
                    hover === true ? "hoverActive" : ""
                  } ${
                    index === activeIndex ? "image-inactive" : "image-active"
                  }`}
                  src={imageSet[1]}
                  alt="test"
                />
              </div>
            ))}
          </>
        )}
      </div>
    </>
  );
};

export default RetouchesCarousel;
