import React, { useCallback, useEffect, useRef, useState } from "react";
import { Link } from "react-router-dom";
import { useMediaQuery } from "@react-hook/media-query";
import { Waypoint } from "react-waypoint";
import ScrollContainer from "react-indiana-drag-scroll";
import { Helmet } from "react-helmet";
import Lottie from "lottie-react";

import { useLocation } from "react-router-dom";

import anime from "animejs/lib/anime.es.js";

import "./galerie.scss";

import Footer from "../Components/Layout/Footer/footer";

import circleArrowLeftBlack from "../Assets/img/landing/circle-arrow-left-black.svg";
import circleArrowRightBlack from "../Assets/img/landing/circle-arrow-right-black.svg";

import boutonSliderBlanc from "../Assets/animations/boutonMenuServices.json";

import { useTranslation } from "react-i18next";

const Galerie = ({ setPageLoad }) => {
  const [selectedLink, setSelectedLink] = useState(
    typeof window !== "undefined" &&
      window.location.state &&
      window.location.state.selectedLink
      ? window.location.state.selectedLink
      : "all"
  );
  const [subCat, setSubCat] = useState("All");

  const titrePageGalerie = useRef();

  const matches = useMediaQuery("only screen and (min-width: 1200px)");

  useEffect(() => {
    window.scrollTo(0, 0);
    setPageLoad(true);

    titrePageGalerie.current.style.transform = "translateY(0%)";
  }, []);

  // Active link
  const [active, setActive] = useState("horizontal");

  const { t, i18n } = useTranslation("gallery");

  console.log(selectedLink);

  return (
    <>
      <Helmet defer={false}>
        <meta charSet="utf-8" />
        <title>E-Do Studio - Galerie shooting Horizontal</title>
        <meta
          name="description"
          content="Explorez les possibilités de productions de contenus photos offertes par notre machine Horizontal."
        />

        {/* <link rel="canonical" href="http://mysite.com/example" /> */}
      </Helmet>
      <div className="pageGalerie">
        <div className="titreAnimationWrapper">
          <h1 className="titrePageGalerie" ref={titrePageGalerie}>
            HORIZONTAL
          </h1>
          <ul>
            <Link to="/galerie">
              <li>all</li>
            </Link>
            <Link to="/galerie-horizontal">
              <li onClick={() => setSubCat("All")} className="active">
                Horizontal
              </li>

              <>
                <ul className="sub-category">
                  <li
                    className={subCat === "Garments" ? "active" : ""}
                    onClick={() => setSelectedLink("Garments")}
                  >
                    — {t("Garments")}
                  </li>
                  <li
                    className={subCat === "Books" ? "active" : ""}
                    onClick={() => setSelectedLink("Books")}
                  >
                    — {t("Books")}
                  </li>
                </ul>
              </>
            </Link>
            <Link to="/galerie-vertical">
              <li>Vertical</li>
              {selectedLink === "vertical" && (
                <ul className="sub-category">
                  <li>— Ghost packshots</li>
                  <li>— Piqués</li>
                </ul>
              )}
            </Link>
            <Link to="/galerie-live">
              <li>Live</li>
              {selectedLink === "live" && (
                <ul className="sub-category">
                  <li>— Garments</li>
                  <li>— Furnitures</li>
                </ul>
              )}
            </Link>
            <Link to="/galerie-eclipse">
              <li>Eclipse</li>
              {selectedLink === "eclipse" && (
                <ul className="sub-category">
                  <li>— Shoes</li>
                  <li>— Bags</li>
                  <li>— Glasses</li>
                  <li>— Cosmetics</li>
                  <li>— Books</li>
                  <li>— Jewelry</li>
                  <li>— Food</li>
                </ul>
              )}
            </Link>
            <Link to="/galerie360">
              <li>360 Interactive</li>
              {selectedLink === "360" && (
                <ul className="sub-category">
                  <li>— Garments</li>
                  <li>— Shoes</li>
                  <li>— Bags</li>
                  <li>— Accessories</li>
                  <li>— Food</li>
                </ul>
              )}
            </Link>
          </ul>
        </div>
        {!matches ? (
          <GalerieMobile
            selectedLink={selectedLink}
            setSelectedLink={setSelectedLink}
            subCat={subCat}
            setSubCat={setSubCat}
          />
        ) : (
          <GalerieDesktop
            selectedLink={selectedLink}
            setSelectedLink={setSelectedLink}
            subCat={subCat}
            setSubCat={setSubCat}
          />
        )}
      </div>
      <Footer AnimationBloc7={true} />
    </>
  );
};

export default Galerie;
