import React from "react";
import { useState } from "react";

// Import des images

// ON MODEL
import image1 from "./img/brown_34_Front.png";
import image2 from "./img/brown_34_inside.jpg";
import image3 from "./lucas.jpg";
import image4 from "./cabines.jpg";

// A PLAT

// Import des icons
import { BsChevronLeft, BsChevronRight } from "react-icons/bs";

const RetouchesCarousel = ({ selectedCat, setSelectedCat }) => {
  const [hover, setIsHover] = useState(false);
  const [isLoading, setIsLoading] = useState(true);

  let imageToShow;

  if (selectedCat === "onModel") {
    imageToShow = hover ? image2 : image1;
  } else if (selectedCat === "aPlat") {
    imageToShow = hover ? image3 : image3;
  } else {
    imageToShow = hover ? image3 : image3;
  }

  console.log(imageToShow);

  const handleImageLoad = () => {
    setIsLoading(false);
  };

  const handleMouseEnter = (image) => {
    setIsHover(true);
    setIsLoading(true);
  };

  const handleMouseLeave = () => {
    setIsHover(null);
    setIsLoading(true);
  };

  console.log(hover);

  return (
    <div
      className="carousel"
      onLoad={handleImageLoad}
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
    >
      <div className="controllers">
        <div className="control left">
          <BsChevronLeft />
        </div>
        <div className="control right">
          <BsChevronRight />
        </div>
      </div>
      {selectedCat === "onModel" && (
        <>
          <div className="images active">
            <img
              className={`imageRetouche ${hover === true ? "noActive" : ""}`}
              src={image1}
              alt="test"
            />
            <img
              className={`hoverImage ${hover === true ? "hoverActive" : ""}`}
              src={image2}
              alt="test"
            />
          </div>
          <div className="images noActive">
            <img
              className={`imageRetouche ${hover === true ? "noActive" : ""}`}
              src={image1}
              alt="test"
            />
            <img
              className={`hoverImage ${hover === true ? "hoverActive" : ""}`}
              src={image2}
              alt="test"
            />
          </div>
        </>
      )}
    </div>
  );
};

export default RetouchesCarousel;
