import React, { useState } from "react";
import { BsChevronLeft, BsChevronRight } from "react-icons/bs";
import image1 from "./img/brown_34_Front.png";
import image2 from "./img/brown_34_inside.jpg";
import image3 from "./lucas.jpg";
import image4 from "./cabines.jpg";

const RetouchesCarousel = ({ selectedCat, setSelectedCat }) => {
  const [hover, setIsHover] = useState(false);
  const [isLoading, setIsLoading] = useState(true);
  const [activeIndex, setActiveIndex] = useState(0);

  const handlePrevClick = () => {
    setActiveIndex((activeIndex - 1 + 3) % 3); // 3 is the number of images in the carousel
  };

  const handleNextClick = () => {
    setActiveIndex((activeIndex + 1) % 3); // 3 is the number of images in the carousel
  };

  const handleImageLoad = () => {
    setIsLoading(false);
  };

  const handleMouseEnter = (image) => {
    setIsHover(true);
    setIsLoading(true);
  };

  const handleMouseLeave = () => {
    setIsHover(null);
    setIsLoading(true);
  };

  const images = [
    {
      id: "1",
      src: image1,
    },
    {
      id: "2",
      src: image2,
    },
    {
      id: "3",
      src: image3,
    },
    {
      id: "4",
      src: image4,
    },
  ];

  const activeImages = images.slice(activeIndex, activeIndex + 3);

  return (
    <div
      className="carousel"
      onLoad={handleImageLoad}
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
    >
      <div className="controllers">
        <div className="control left" onClick={handlePrevClick}>
          <BsChevronLeft />
        </div>
        <div className="control right" onClick={handleNextClick}>
          <BsChevronRight />
        </div>
      </div>
      {selectedCat === "onModel" && (
        <>
          {activeImages.map((image) => (
            <div key={image.id} className="images active">
              <img
                className={`imageRetouche ${hover === true ? "noActive" : ""}`}
                src={image.src}
                alt="test"
              />
              <img
                className={`hoverImage ${hover === true ? "hoverActive" : ""}`}
                src={image.src}
                alt="test"
              />
            </div>
          ))}
        </>
      )}
    </div>
  );
};

export default RetouchesCarousel;
