import React, { useEffect, useState } from "react";
import { Redirect } from "react-router-dom";
import { useMediaQuery } from "@react-hook/media-query";
import PropTypes from "prop-types";
import axios from "axios";
import { Helmet } from "react-helmet";

import Footer from "../../Components/Layout/Footer/footer";

import { Formik, Form, Field } from "formik";
import nodemailer from "nodemailer";

import "./contact.scss";

const NosCoordonnees = () => {
  return (
    <div className="PC_NosCoordonnees">
      <div className="PC_NosCoordonneesText">
        <span>Nos coordonnées</span>
      </div>
      <div className="PC_NosCoordonneesFleche">
        <svg width="14" height="31" xmlns="http://www.w3.org/2000/svg">
          <path
            stroke="#000"
            fill="none"
            d="M7.12.94l-.5 29.17M.62 23.99l6 6 6-6"
          />
        </svg>
      </div>
    </div>
  );
};

const Contact = ({ setPageLoad }) => {
  const [formData, setFormData] = useState({});
  const [formSend, setFormSend] = useState(false);
  const [redirect, setRedirect] = useState(false);

  const [formSendOn, setFormSendOn] = useState(false);

  const matches = useMediaQuery("only screen and (min-width: 992px)");

  useEffect(() => {
    window.scrollTo(0, 0);
  }, []);

  const handleFormSubmit = (e) => {
    e.preventDefault();

    if (!formData.nom || formData.nom.length === 0) {
      document.querySelector(".PC_FormGlobalNom").style.borderBottom =
        "1px solid red";
    } else {
      document.querySelector(".PC_FormGlobalNom").style.borderBottom =
        "1px solid black";
    }

    if (!formData.prenom || formData.prenom.length === 0) {
      document.querySelector(".PC_FormGlobalPrenom").style.borderBottom =
        "1px solid red";
    } else {
      document.querySelector(".PC_FormGlobalPrenom").style.borderBottom =
        "1px solid black";
    }

    if (!formData.email || formData.email.length === 0) {
      document.querySelector(".PC_FormGlobalMail").style.borderBottom =
        "1px solid red";
    } else {
      document.querySelector(".PC_FormGlobalMail").style.borderBottom =
        "1px solid black";
    }

    if (!formData.message || formData.message.length === 0) {
      document.querySelector(".PC_FormGlobalMessage").style.borderBottom =
        "1px solid red";
    } else {
      document.querySelector(".PC_FormGlobalMessage").style.borderBottom =
        "1px solid black";
    }

    if ((formData.nom, formData.prenom, formData.email, formData.message)) {
      if (
        formData.nom.length >= 2 &&
        formData.prenom.length >= 2 &&
        formData.email.length >= 2 &&
        formData.message.length >= 2
      ) {
        if (!formSendOn) {
          setFormSendOn(true);

          formData.clePrive =
            "gerg5e4gt64!gte$gerg&f6zef5bFnuefIFUEZAIFuhjIHIFGi$fzefzgGRf6ze5f4ifElseELZ";
          axios({
            method: "post",
            url: "'http://localhost/sendMail.php'",
            headers: { "content-type": "application/json" },
            data: formData,
          })
            .then((result) => {
              if (result.data.sent) {
                window.scrollTo(0, 0);
                setFormSendOn(false);
                setRedirect(true);
              } else {
                setFormSendOn(false);
              }
            })
            .catch();
        }
      }
    }
  };

  const handleChange = (e, field) => {
    let value = e.target.value;

    setFormData({
      ...formData,
      [field]: value,
    });
  };

  useEffect(() => {
    setPageLoad(true);
  }, []);

  const handleSubmit = (values) => {
    console.log("Form values: ", values);
    // code pour envoyer les données via votre backend ici
  };

  const nodemailer = require("nodemailer");

  let transporter = nodemailer.createTransport({
    host: "smtp.hostinger.fr",
    port: 465,
    secure: false,
    auth: {
      user: "contact@e-do.studio",
      pass: "OnlyUs69!",
    },
  });

  let mailOptions = {
    from: '"Nom de l\'expéditeur" <monadressemail@hostinger.fr>',
    to: "destinataire@domaine.com",
    subject: "Objet du message",
    text: "Contenu du message",
    html: "<p>Contenu du message</p>",
  };

  transporter.sendMail(mailOptions, (error, info) => {
    if (error) {
      return console.log(error);
    }
    console.log("Message envoyé: %s", info.messageId);
    console.log("Preview URL: %s", nodemailer.getTestMessageUrl(info));
  });

  return (
    <>
      <Helmet defer={false}>
        <meta charSet="utf-8" />
        <title>E-Do Studio - Nous contacter</title>
        <meta
          name="description"
          content="Besoin d’informations à propos de notre offre et nos équipement? Vous souhaitez nous contacter pour un devis? Nous vous invitons à remplir notre formulaire de contact."
        />

        {/* <link rel="canonical" href="http://mysite.com/example" /> */}
      </Helmet>

      <div className="PageContact">
        <div className="PC_Global">
          <div className="PC_GlobalText">
            <div className="PC_GlobalTextHaut">
              <h1>Vous avez des questions ?</h1>
              <h2>Des besoins spécifiques ?</h2>
              <p>
                N’hésitez pas à nous contacter en remplissant le formulaire
                ci-contre et nous vous répondrons dans les meilleurs délais.
              </p>
            </div>

            {matches ? (
              <div className="PC_GlobalTextBas">
                <NosCoordonnees />
              </div>
            ) : null}
          </div>

          <div className="PC_GlobalForm">
            <Formik
              initialValues={{
                nom: "",
                prenom: "",
                tel: "",
                societe: "",
                email: "",
                site: "",
                age: "",
                message: "",
              }}
              onSubmit={handleSubmit}
            >
              {({ handleSubmit }) => (
                <Form>
                  <div className="PC_GlobalForm_Infos">
                    <Field
                      type="text"
                      name="nom"
                      placeholder="Nom*"
                      className="PC_FormGlobal PC_FormGlobalNom"
                      required
                    />
                    <Field
                      type="text"
                      name="prenom"
                      placeholder="Prénom*"
                      className="PC_FormGlobal PC_FormGlobalPrenom"
                      required
                    />
                    <Field
                      type="text"
                      name="tel"
                      placeholder="Téléphone"
                      className="PC_FormGlobal PC_FormGlobalTelephone"
                      required
                    />
                    <Field
                      type="text"
                      name="societe"
                      placeholder="Société"
                      className="PC_FormGlobal"
                    />
                    <Field
                      type="email"
                      name="email"
                      placeholder="E-mail*"
                      className="PC_FormGlobal PC_FormGlobalMail"
                      required
                    />
                    <Field
                      type="text"
                      name="site"
                      placeholder="Site web"
                      className="PC_FormGlobal"
                    />
                    <Field
                      type="hidden"
                      name="age"
                      placeholder="Site web"
                      className="PC_FormGlobal"
                    />
                  </div>
                  <div className="PC_GlobalForm_Message">
                    <Field
                      as="textarea"
                      name="message"
                      placeholder="Vous avez une idée de projet, besoin d’un devis, d’une prise de rendez-vous ? Envoyez-nous votre message."
                      className="PC_FormGlobal PC_FormGlobalMessage"
                      required
                    />

                    <div className="PC_FormButtonWrapper">
                      <button type="submit">
                        <div>Envoyer</div>
                        <svg
                          xmlns="http://www.w3.org/2000/svg"
                          viewBox="0 0 17.63 12.64"
                        >
                          <path
                            fill="none"
                            stroke="#fff"
                            d="M0 6.53l17.04.01"
                          />
                          <path
                            fill="none"
                            stroke="#fff"
                            d="M10.86.35l6.06 6.14-6 5.79"
                          />
                        </svg>
                      </button>
                    </div>
                  </div>
                </Form>
              )}
            </Formik>
          </div>
        </div>
      </div>

      {!matches ? (
        <div className="PC_GlobalTextBas">
          <NosCoordonnees />
        </div>
      ) : null}

      {redirect ? <Redirect to="/merci" /> : ""}

      <Footer AnimationBloc7={true} />
    </>
  );
};

export default Contact;
