            {selectedCat === "lunettes" && (
              <>
                {images.map((imageSet, index) => (
                  <div
                    className={`imagesContainer ${
                      index === activeIndex ? "active" : "noActive"
                    }`}
                    key={index}
                    ref={containerRef}
                  >
                    <img
                      className={`imageRetouche ${
                        hover === true ? "noActive" : ""
                      } image-transition`}
                      src={imageSet[0]}
                      alt="test"
                      onMouseEnter={handleMouseEnter}
                      onMouseLeave={handleMouseLeave}
                    />
