          haut="22"
          ajustHauteurTop=""
          ajustHauteurBottom="7"
          anim={1}
          scrollX={scrollX}
        />
        <IMGPC
          linkUrl="/service-mannequin-vertical"
          src="pantalon-le-kasha.jpg"
          lar="18"
          haut="27"
          ajustHauteurTop="7"
          ajustHauteurBottom=""
          anim={1}
          scrollX={scrollX}
        />
        <IMGPC
          linkUrl="/service-mannequin-vertical"
          src="veste-de-fursac.png"
          lar="13"
          haut="20"
          ajustHauteurTop="15"
          ajustHauteurBottom=""
          anim={1}
          scrollX={scrollX}
        />
        <IMGPC
          linkUrl="/service-mannequin-vertical"
          src="col-roule-vuarnet-boramy.png"
          lar="18"
          haut="27"
          ajustHauteurTop="7"
          ajustHauteurBottom=""
          anim={1}
          scrollX={scrollX}
        />
        <IMGPC
          linkUrl="/service-mannequin-vertical"
          src="echarpe-vuarnet.png"
          lar="13"
          haut="22"
          ajustHauteurTop=""
          ajustHauteurBottom="14"
          anim={1}
          scrollX={scrollX}
        />
        <IMGPC
          linkUrl="/service-mannequin-vertical"
          src="gilet-hit-air.jpg"
          lar="15"
          haut="20"
          ajustHauteurTop=""
          ajustHauteurBottom="20"
          anim={1}
          scrollX={scrollX}
        />
