import React, { useCallback, useEffect, useRef, useState } from "react";
import { Link } from "react-router-dom";
import { useMediaQuery } from "@react-hook/media-query";
import { Waypoint } from "react-waypoint";
import ScrollContainer from "react-indiana-drag-scroll";
import { Helmet } from "react-helmet";
import Lottie from "lottie-react";

import GalerieHorizontal from "./galerie-horizontal";

const GalerieCategorie = ({ setPageLoad, selectedLink, setSelectedLink }) => {
  const [scrollX, setScrollX] = useState(0);

  const handleScroll = (event) => {
    setScrollX(event);
  };

  const PMS_BoutonPCNextButton = useRef();
  const PMS_BoutonPCPrecButton = useRef();

  const sliderNavSuiv = () => {
    PMS_BoutonPCNextButton.current.play();
    setTimeout(() => {
      PMS_BoutonPCNextButton.current.stop();
    }, 600);
  };

  const sliderNavPrec = () => {
    PMS_BoutonPCPrecButton.current.play();
    setTimeout(() => {
      PMS_BoutonPCPrecButton.current.stop();
    }, 600);
  };

  function scrollLeft() {
    const scrollBox = document.getElementsByClassName("galeriePCWrapper")[0];
    sliderNavPrec();
    scrollBox.scrollBy({
      left: -500,
      behavior: "smooth",
    });
  }

  function scrollRight() {
    const scrollBox = document.getElementsByClassName("galeriePCWrapper")[0];
    sliderNavSuiv();
    scrollBox.scrollBy({
      left: 500,
      behavior: "smooth",
    });
  }

  useEffect(() => {
    const scrollBox = document.getElementsByClassName("galeriePCWrapper")[0];

    const keyScroll = (e) => {
      const key = e.keyCode;
      if (key == "39") {
        scrollBox.scrollBy({
          left: 500,
          behavior: "smooth",
        });
      } else if (key == "37") {
        scrollBox.scrollBy({
          left: -500,
          behavior: "smooth",
        });
      }
    };

    document.addEventListener("keydown", keyScroll);
  }, []);

  useEffect(() => {
    anime({
      targets: ".PMS_BoutonPCNextButton",
      opacity: [0, 1],
      easing: "easeInOutSine",
      duration: 500,
      delay: 300,
    });

    anime({
      targets: ".PMS_BoutonPCPrecButton",
      opacity: [0, 1],
      easing: "easeInOutSine",
      duration: 500,
      delay: 300,
    });

    //Animation du bouton
  }, []);
  const titrePageGalerie = useRef();

  const matches = useMediaQuery("only screen and (min-width: 1200px)");

  useEffect(() => {
    window.scrollTo(0, 0);
    setPageLoad(true);

    titrePageGalerie.current.style.transform = "translateY(0%)";
  }, []);

  const [displayHorizontal, setDisplayHorizontal] = useState(false);
  const [displayVertical, setDisplayVertical] = useState(false);

  const handleClickHorizontal = (event) => {
    event.preventDefault();
    setDisplayHorizontal(!displayHorizontal);
  };

  const handleClickVertical = (event) => {
    event.preventDefault();
    setDisplayVertical(!displayVertical);
  };

  console.log(selectedLink);

  return (
    <>
      <Helmet defer={false}>
        <meta charSet="utf-8" />
        <title>E-Do Studio - Galerie shooting produits</title>
        <meta
          name="description"
          content="Explorez les possibilités de productions de contenus photos et vidéos offertes par nos services."
        />

        {/* <link rel="canonical" href="http://mysite.com/example" /> */}
      </Helmet>
      <div className="pageGalerie">
        <div className="titreAnimationWrapper">
          <h1 className="titrePageGalerie" ref={titrePageGalerie}>
            GALERIE
          </h1>
          <ul>
            <Link
              to={{
                pathname: "/galerie",
                state: { selectedLink: "all" },
              }}
            >
              <li className="active">all</li>
            </Link>
            <Link
              to={{
                pathname: "/galerie-horizontal",
                state: { selectedLink: "horizontal" },
              }}
              selectedLink={selectedLink}
              setSelectedLink={setSelectedLink}
              onClick={() => setSelectedLink("horizontal")}
            >
              <li>
                Horizontal<span>+</span>{" "}
              </li>
            </Link>
            <Link
              to={{
                pathname: "/galerie-vertical",
                state: { selectedLink: "vertical" },
              }}
              onClick={() => setSelectedLink("vertical")}
            >
              <li>Vertical</li>
            </Link>
            <Link
              to={{
                pathname: "/galerie-live",
                state: { selectedLink: "live" },
              }}
              onClick={() => setSelectedLink("live")}
            >
              <li>Live</li>
            </Link>
            <Link
              to={{
                pathname: "/galerie-eclipse",
                state: { selectedLink: "eclipse" },
              }}
              onClick={() => setSelectedLink("eclipse")}
            >
              <li>Eclipse</li>
            </Link>
            <Link
              to={{
                pathname: "/galerie360",
                state: { selectedLink: "360" },
              }}
              onClick={() => setSelectedLink("360")}
            >
              <li>360</li>
            </Link>
          </ul>
        </div>
        );
      </div>

      <GalerieHorizontal />
    </>
  );
};

export default GalerieCategorie;
