import React from "react";
import { useState } from "react";

// Import des images

// ON MODEL
import image1 from "./img/brown_34_Front.png";
import image2 from "./img/brown_34_inside.jpg";
import image3 from "./lucas.jpg";
import image4 from "./cabines.jpg";

// A PLAT

// Import des icons

import { BsChevronLeft, BsChevronRight } from "react-icons/bs";

const RetouchesCarousel = ({ selectedCat, setSelectedCat }) => {
  let imageToShow;

  switch (selectedCat) {
    case "onModel":
      imageToShow = hover ? image2 : image1;
      break;
    case "aPlat":
      imageToShow = hover ? image4 : image3;
      break;
    default:
      imageToShow = hover ? image2 : image1;
  }

  const handleImageLoad = () => {
    setIsLoading(false);
  };

  const handleMouseEnter = (image) => {
    setIsHover(image);
    setIsLoading(true);
  };

  const handleMouseLeave = () => {
    setIsHover(null);
    setIsLoading(true);
  };

  const [hover, setIsHover] = useState(false);
  const [isLoading, setIsLoading] = useState(true);

  return (
    <div
      className="carousel"
      onLoad={handleImageLoad}
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
    >
      <div className="controllers">
        <div className="control left">
          <BsChevronLeft />
        </div>
        <div className="control right">
          <BsChevronRight />
        </div>
      </div>
      <img
        className={`imageRetouche ${isLoading ? "loading" : "loaded"}`}
        src={hover ? image2 : image1}
        alt="test"
      />
    </div>
  );
};

export default RetouchesCarousel;
