import i18next from "i18next";
import { initReactI18next } from "react-i18next";

// import your translation files
import menuEn from "./translation/en/menu.json";
import menuFr from "./translation/fr/menu.json";

import argument1Fr from "./translation/fr/argument1.json";
import argument1En from "./translation/fr/argument1.json";

// initialize the i18next library
i18next.use(initReactI18next).init({
  resources: {
    fr: {
      menu: menuFr,
      argument: argument1,
    },
    en: {
      menu: menuEn,
      argument: argument1,
    },
  },
  lng: "fr",
  fallbackLng: "en",
  interpolation: {
    escapeValue: false,
  },
});

export default i18next;
