import { useTexture } from "@react-three/drei";
import { memo } from "react";
import { PlaneGeometry, Mesh, MeshBasicMaterial } from "three";
import image from "./test.png";

export const Photo = memo(({ x, y }) => {
  const texture = useTexture(image);

  // Réinitialiser les réglages de répétition de la texture
  texture.wrapS = texture.wrapT = undefined;
  texture.repeat.set(1, 1);

  // Utiliser PlaneGeometry avec les dimensions de l'image
  const imageWidth = texture.image.width;
  const imageHeight = texture.image.height;
  const geometry = new PlaneGeometry(imageWidth, imageHeight);

  return (
    <mesh position={[x, y, 0]} geometry={geometry}>
      <meshBasicMaterial map={texture} />
    </mesh>
  );
});
