import React, { useEffect } from "react";
import { Helmet } from "react-helmet";
import Footer from "../Components/Layout/Footer/footer";
import axios from "axios";

import "./reservation.scss";

const Reservation = ({ setPageLoad }) => {
  const [iframeContent, setIframeContent] = useState("");

  useEffect(() => {
    window.scrollTo(0, 0);
    setPageLoad(true);
    fetchIframeContent();
  }, []);

  const fetchIframeContent = async () => {
    const response = await axios.get("https://your-iframe-source.com");
    const iframeHTML = response.data;
    translateIframeContent(iframeHTML);
  };

  const translateIframeContent = async (content) => {
    const response = await axios.post(
      "https://api.deepl.com/v2/translate",
      {
        source_lang: "en",
        target_lang: "fr",
        text: content,
      },
      {
        headers: {
          "Content-Type": "application/json",
          Authorization: "Bearer YOUR_DEEPL_API_KEY",
        },
      }
    );
    const translatedHTML = response.data.translations[0].text;
    setIframeContent(translatedHTML);
  };

  return (
    <>
      <Helmet defer={false}>
        <title>E-Do Studio - Réservation - réservation en ligne</title>
        <meta
          name="description"
          content="Pour vos shootings à la journée ou demi-journée, effectuez une demande de réservation en choisissant vos dates et horaires ci-dessous. Si vous avez des questions rendez-vous sur la page Contact, Quentin ou Victor vous répondrons très rapidement."
        />
        <script
          src="https://embed.acuityscheduling.com/js/embed.js"
          type="text/javascript"
        />
      </Helmet>
      <div className="pageReservation">
        <iframe
          srcDoc={iframeContent}
          title="Prendre rendez-vous"
          width="100%"
          height="100%"
          frameBorder="0"
        ></iframe>
      </div>
      <Footer AnimationBloc7={true} />
    </>
  );
};

export default Reservation;
