import React, { useState } from "react";
import { BsChevronLeft, BsChevronRight } from "react-icons/bs";
import image1 from "./img/brown_34_Front.png";
import image2 from "./img/brown_34_inside.jpg";
import image3 from "./img/Lola_Front2.jpg";
import image4 from "./img/Lola_Front.pmg";
import image5 from "./img/suit_jacket _Inside.jpg";
import image6 from "./img/suitcomplete_Front.jpg";

const RetouchesCarousel = ({ selectedCat, setSelectedCat }) => {
  const [hover, setIsHover] = useState(false);
  const [isLoading, setIsLoading] = useState(true);
  const [activeIndex, setActiveIndex] = useState(0); // nouvel état pour suivre l'indice de l'élément actif

  const images = [
    [image1, image2],
    [image3, image4],
  ];

  const handleImageLoad = () => {
    setIsLoading(false);
  };

  const handleMouseEnter = (image) => {
    setIsHover(true);
    setIsLoading(true);
  };

  const handleMouseLeave = () => {
    setIsHover(null);
    setIsLoading(true);
  };

  const handleLeftClick = () => {
    setActiveIndex(activeIndex - 1 < 0 ? images.length - 1 : activeIndex - 1);
  };

  const handleRightClick = () => {
    setActiveIndex(activeIndex + 1 >= images.length ? 0 : activeIndex + 1);
  };

  return (
    <div
      className="carousel"
      onLoad={handleImageLoad}
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
    >
      <div className="controllers">
        <div className="control left" onClick={handleLeftClick}>
          <BsChevronLeft />
        </div>
        <div className="control right" onClick={handleRightClick}>
          <BsChevronRight />
        </div>
      </div>
      {selectedCat === "onModel" && (
        <>
          {images.map((imageSet, index) => (
            <div
              className={`images ${
                index === activeIndex ? "active" : "noActive"
              }`}
              key={index}
            >
              <img
                className={`imageRetouche ${hover === true ? "noActive" : ""}`}
                src={imageSet[0]}
                alt="test"
              />
              <img
                className={`hoverImage ${hover === true ? "hoverActive" : ""}`}
                src={imageSet[1]}
                alt="test"
              />
            </div>
          ))}
        </>
      )}
    </div>
  );
};

export default RetouchesCarousel;
