import React, { useState, useRef, useEffect } from "react";
import { BsChevronLeft, BsChevronRight } from "react-icons/bs";

import image1 from "./img/brown_34_Front.png";
import image2 from "./img/brown_34_inside.jpg";
import image3 from "./img/Lola_Front.png";
import image4 from "./img/Lola_Front2.jpg";
import image5 from "./img/suitcomplete_Front.png";
import image6 from "./img/suit_jacket_Inside.jpg";
import image7 from "./img/bottes_rose_before.jpg";
import image8 from "./img/bottes_rose_after.png";

const RetouchesCarousel = ({ selectedCat, setSelectedCat }) => {
  const [hover, setIsHover] = useState(false);
  const [isLoading, setIsLoading] = useState(true);
  const [activeIndex, setActiveIndex] = useState(0); // nouvel état pour suivre l'indice de l'élément actif

  let images = [];

  if (selectedCat === "onModel") {
    images = [
      [image1, image2],
      [image3, image4],
      [image5, image6],
    ];
  } else if (selectedCat === "access") {
    images = [
      [image7, image8],
      // [image9, image10],
      // [image11, image12],
    ];
  } else {
    // fallback to default images
    images = [
      [image1, image2],
      [image3, image4],
      [image5, image6],
    ];
  }

  const handleImageLoad = () => {
    setIsLoading(false);
  };

  const handleMouseEnter = (image) => {
    setIsHover(true);
    setIsLoading(true);
  };

  const handleMouseLeave = () => {
    setIsHover(null);
    setIsLoading(true);
  };

  const handleLeftClick = () => {
    setActiveIndex(activeIndex - 1 < 0 ? images.length - 1 : activeIndex - 1);
  };

  const handleRightClick = () => {
    setActiveIndex(activeIndex + 1 >= images.length ? 0 : activeIndex + 1);
  };

  const containerRef = useRef(null);

  const handleKeyDown = (event) => {
    console.log(event.key);
    if (event.key === "ArrowLeft") {
      handleLeftClick();
    } else if (event.key === "ArrowRight") {
      handleRightClick();
    }
  };

  useEffect(() => {
    setActiveIndex(0);
    containerRef.current.focus();
  }, [selectedCat]);

  const imageRefs = useRef([]);

  useEffect(() => {
    const currentImage = imageRefs.current[activeIndex].current;

    // Appliquer l'animation
    currentImage.style.opacity = 0;
    setTimeout(() => {
      currentImage.style.opacity = 1;
    }, 500);
  }, [activeIndex]);

  // Dans ce composant react qui retourne un carousel
  // Je souhaites ajouter une animation de transition entre les images

  const animationBetweenImages = () => {
    const currentImageRef = imageRefs.current[activeIndex];

    if (currentImageRef) {
      const currentImage = currentImageRef.current;

      // Appliquer l'animation
      currentImage.style.opacity = 0;
      setTimeout(() => {
        currentImage.style.opacity = 1;
      }, 500);
    }
  };

  return (
    <>
      <div
        className="carousel"
        ref={containerRef}
        onKeyDown={handleKeyDown}
        tabIndex={0}
      >
        {selectedCat === "onModel" && (
          <>
            {images.map((imageSet, index) => (
              <div
                className={`images ${
                  index === activeIndex ? "active" : "noActive"
                }`}
                key={index}
                onLoad={handleImageLoad}
                onMouseEnter={handleMouseEnter}
                onMouseLeave={handleMouseLeave}
              >
                <div className="controllers">
                  <div
                    className="control leftControl"
                    onClick={handleLeftClick}
                  >
                    <BsChevronLeft />
                  </div>
                  <div
                    className="control rightControl"
                    onClick={handleRightClick}
                  >
                    <BsChevronRight />
                  </div>
                </div>
                <img
                  className={`imageRetouche ${
                    hover === true ? "noActive" : ""
                  }`}
                  src={imageSet[0]}
                  alt="test"
                  ref={imageRefs}
                />
                <img
                  className={`hoverImage ${
                    hover === true ? "hoverActive" : ""
                  }`}
                  src={imageSet[1]}
                  alt="test"
                  ref={imageRefs}
                />
                <p>
                  {hover === true ? (
                    <p>Après retouche</p>
                  ) : (
                    <p>Avant retouche</p>
                  )}
                </p>
              </div>
            ))}
          </>
        )}
        {selectedCat === "access" && (
          <>
            {images.map((imageSet, index) => (
              <div
                className={`images ${
                  index === activeIndex ? "active" : "noActive"
                }`}
                key={index}
              >
                <img
                  className={`imageRetouche ${
                    hover === true ? "noActive" : ""
                  }`}
                  src={imageSet[0]}
                  alt="test"
                />
                <img
                  className={`hoverImage ${
                    hover === true ? "hoverActive" : ""
                  }`}
                  src={imageSet[1]}
                  alt="test"
                />
              </div>
            ))}
          </>
        )}
      </div>
    </>
  );
};

export default RetouchesCarousel;
