    ];
  }

  const handleMouseEnter = (image) => {
    setIsHover(true);
  };

  const handleMouseLeave = () => {
    setIsHover(null);
  };

  const handleLeftClick = () => {
    const lastIndex = images.length - 1;
    const index = activeIndex === 0 ? lastIndex : activeIndex - 1;
    setActiveIndex(index);

    const currentImage = containerRef.current.children[index];
    const previousIndex = index === lastIndex ? 0 : index + 1;
    const previousImage = containerRef.current.children[previousIndex];

    // Animation between images
    previousImage.style.transform = "translateX(0%) scale(1)";
    currentImage.style.transform = "translateX(-100%) scale(1.2)";
    currentImage.style.transition = "transform 1s"; // add transition to current image

    // Reset transform after transition
    currentImage.addEventListener(
      "transitionend",
      () => {
        currentImage.style.transition = ""; // remove transition from current image
        currentImage.style.transform = "translateX(0%) scale(1)";
      },
      { once: true }
    );

    setTimeout(() => {
      previousImage.style.transition = "transform 1s";
      previousImage.style.transform = "translateX(100%) scale(0.8)";
    }, 0);
  };

  const handleRightClick = () => {
    const lastIndex = images.length - 1;
    const index = activeIndex === lastIndex ? 0 : activeIndex + 1;
    setActiveIndex(index);

    const currentImage = containerRef.current.children[index];
    const previousIndex = index === 0 ? lastIndex : index - 1;
    const previousImage = containerRef.current.children[previousIndex];

    // Animation between images
    previousImage.style.transform = "translateX(0%) scale(1)";
    currentImage.style.transform = "translateX(100%) scale(1.2)";
    currentImage.style.transition = "transform 1s"; // add transition to current image

    setTimeout(() => {
      previousImage.style.transition = "transform 1s";
      previousImage.style.transform = "translateX(-100%) scale(0.8)";
      currentImage.style.transform = "translateX(0%) scale(1)";
    }, 0);
  };

  const containerRef = useRef(null);

  const handleKeyDown = (event) => {
    console.log(event.key);
    if (event.key === "ArrowLeft") {
      handleLeftClick();
    } else if (event.key === "ArrowRight") {
      handleRightClick();
    }
  };

  useEffect(() => {
    setActiveIndex(0);
    containerRef.current.focus();
  }, [selectedCat]);

  return (
    <>
      <div className="carousel" onKeyDown={handleKeyDown} tabIndex={0}>
        <div
          className="controllers"
          onMouseEnter={handleMouseEnter}
          onMouseLeave={handleMouseLeave}
