import React, { useState, useRef, useEffect } from "react";
import { BsChevronLeft, BsChevronRight } from "react-icons/bs";

import image1 from "./img/brown_34_Front.png";
import image2 from "./img/brown_34_inside.jpg";
import image3 from "./img/Lola_Front.png";
import image4 from "./img/Lola_Front2.jpg";
import image5 from "./img/suitcomplete_Front.png";
import image6 from "./img/suit_jacket_Inside.jpg";
import image7 from "./img/bottes_rose_before.jpg";
import image8 from "./img/bottes_rose_after.png";

const RetouchesCarousel = ({ selectedCat, setSelectedCat }) => {
  const [hover, setIsHover] = useState(false);
  const [isLoading, setIsLoading] = useState(true);
  const [activeIndex, setActiveIndex] = useState(0); // nouvel état pour suivre l'indice de l'élément actif

  let images = [];

  if (selectedCat === "onModel") {
    images = [
      [image1, image2],
      [image3, image4],
      [image5, image6],
    ];
  } else if (selectedCat === "access") {
    images = [
      [image7, image8],
      // [image9, image10],
      // [image11, image12],
    ];
  } else {
    // fallback to default images
    images = [
      [image1, image2],
      [image3, image4],
      [image5, image6],
    ];
  }

  const handleImageLoad = () => {
    setIsLoading(false);
  };

  const handleMouseEnter = (image) => {
    setIsHover(true);
    setIsLoading(true);
  };

  const handleMouseLeave = () => {
    setIsHover(null);
    setIsLoading(true);
  };

  const handleLeftClick = () => {
    const lastIndex = images.length - 1;
    const index = activeIndex === 0 ? lastIndex : activeIndex - 1;
    setActiveIndex(index);

    const currentImage = containerRef.current.children[index];
    const previousIndex = index === lastIndex ? 0 : index + 1;
    const previousImage = containerRef.current.children[previousIndex];

    previousImage.style.opacity = 1; // rendre l'image précédente visible
    currentImage.style.opacity = 0; // cacher l'image courante

    setTimeout(() => {
      previousImage.style.transition = "opacity 2s"; // ajouter une transition à l'image précédente
      previousImage.style.opacity = 0; // cacher l'image précédente
      currentImage.style.transition = "opacity 2s"; // ajouter une transition à l'image courante
      currentImage.style.opacity = 1; // rendre l'image courante visible
    }, 0);
  };

  const handleRightClick = () => {
    const lastIndex = images.length - 1;
    const index = activeIndex === lastIndex ? 0 : activeIndex + 1;
    setActiveIndex(index);

    const currentImage = containerRef.current.children[index];
    const previousIndex = index === 0 ? lastIndex : index - 1;
    const previousImage = containerRef.current.children[previousIndex];

    previousImage.style.opacity = 1; // rendre l'image précédente visible
    currentImage.style.opacity = 0; // cacher l'image courante

    setTimeout(() => {
      previousImage.style.transition = "opacity 1s"; // ajouter une transition à l'image précédente
      previousImage.style.opacity = 0; // cacher l'image précédente
      currentImage.style.transition = "opacity 1s"; // ajouter une transition à l'image courante
      currentImage.style.opacity = 1; // rendre l'image courante visible
    }, 0);
  };

  const containerRef = useRef(null);

  const handleKeyDown = (event) => {
    console.log(event.key);
    if (event.key === "ArrowLeft") {
      handleLeftClick();
    } else if (event.key === "ArrowRight") {
      handleRightClick();
    }
  };

  useEffect(() => {
    setActiveIndex(0);
    containerRef.current.focus();
  }, [selectedCat]);

  return (
    <>
      <div
        className="carousel"
        ref={containerRef}
        onKeyDown={handleKeyDown}
        tabIndex={0}
      >
        {selectedCat === "onModel" && (
          <>
            {images.map((imageSet, index) => (
              <div
                className={`images ${
                  index === activeIndex ? "active" : "noActive"
                }`}
                key={index}
                onLoad={handleImageLoad}
                onMouseEnter={handleMouseEnter}
                onMouseLeave={handleMouseLeave}
              >
                <div className="controllers">
                  <div
                    className="control leftControl"
                    onClick={handleLeftClick}
                  >
                    <BsChevronLeft />
                  </div>
                  <div
                    className="control rightControl"
                    onClick={handleRightClick}
                  >
                    <BsChevronRight />
                  </div>
                </div>
                <img
                  className={`imageRetouche ${
                    hover === true ? "noActive" : ""
                  } image-transition`}
                  src={imageSet[0]}
                  alt="test"
                />
                <img
                  className={`hoverImage ${
                    hover === true ? "hoverActive" : ""
                  } image-transition`}
                  src={imageSet[1]}
                  alt="test"
                />
                <p>
                  {hover === true ? (
                    <p>Après retouche</p>
                  ) : (
                    <p>Avant retouche</p>
                  )}
                </p>
              </div>
            ))}
          </>
        )}
        {selectedCat === "access" && (
          <>
            {images.map((imageSet, index) => (
              <div
                className={`images ${
                  index === activeIndex ? "active" : "noActive"
                }`}
                key={index}
              >
                <img
                  className={`imageRetouche ${
                    hover === true ? "noActive" : ""
                  }`}
                  src={imageSet[0]}
                  alt="test"
                />
                <img
                  className={`hoverImage ${
                    hover === true ? "hoverActive" : ""
                  }`}
                  src={imageSet[1]}
                  alt="test"
                />
              </div>
            ))}
          </>
        )}
      </div>
    </>
  );
};

export default RetouchesCarousel;
