                        style={{
                          width: "80%",
                          height: "450px",
                          display: "flex",
                          flexDirection: "column",
                          alignItems: "center",
                          margin: "0 auto",
                          objectFit: "contain",
                        }}
                      />
                      {hover === true ? (
                        <p style={{ paddingTop: "10px", textAlign: "center" }}>
                          {t("After")}
                        </p>
                      ) : (
                        <p style={{ paddingTop: "10px", textAlign: "center" }}>
                          {t("Before")}
                        </p>
                      )}
                    </div>
                  ))}
                </Slider>
              </>
            )}
            {selectedCat === "plat" && (
              <>
                <Slider
                  {...sliderTopSettings}
                  ref={sliderTop}
                  className="SliderTop"
                >
                  {images.map((imageSet, index) => (
                    <div
                      className="sliderTopUnique retouche-carousel"
                      key={index}
                    >
                      <img
                        className="sliderTopUnique_IMG"
                        src={!hover === true ? imageSet[0] : imageSet[1]}
                        alt="retouche image on model ghost"
                        onMouseEnter={handleMouseEnter}
                        onMouseLeave={handleMouseLeave}
                        style={{
                          width: "80%",
                          height: "450px",
                          display: "flex",
                          flexDirection: "column",
                          alignItems: "center",
                          margin: "0 auto",
                          objectFit: "contain",
                        }}
                      />
                      {hover === true ? (
                        <p style={{ paddingTop: "10px", textAlign: "center" }}>
                          {t("After")}
                        </p>
                      ) : (
                        <p style={{ paddingTop: "10px", textAlign: "center" }}>
                          {t("Before")}
                        </p>
                      )}
                    </div>
                  ))}
                </Slider>
              </>
            )}
          </div>
        </>
      )}
    </>
  );
};

export default RetouchesCarousel;
