import React, {useState, useRef, useEffect} from "react"
import ReactDOM from "react-dom"
import { BrowserRouter, Route, Switch, Redirect } from "react-router-dom"
import {Helmet} from "react-helmet";

import 'Assets/vendor/normalize.css'
import 'Assets/scss/index.scss'
import './index.scss'

import ViewportProvider from 'Hooks/viewportProvider'

import Navbar from './Components/Layout/Navbar/navbar'

import Index from "Views/Index/index"
import ServicesMenu from "Views/servicesMenu"
import Service from "Views/Service/service"
import Galerie from "Views/galerie"
import GalerieHorizontal from "Views/galerie-horizontal"
import GalerieVertical from "Views/galerie-vertical"
import GalerieLive from "Views/galerie-live"
import GalerieEclipse from "Views/galerie-eclipse"
import Galerie360 from "Views/galerie360"
import Contact from "Views/Contact/contact"
import Merci from "Views/Contact/merci"
import Mentionslegales from "Views/mentionslegales"
import SiteAgence from "Views/agence"
import Reservation from "Views/reservation"
import Landing from "Views/Landing/landing"
import Cyclorama from "Views/Service/cyclorama"
import Blog from "Views/blog"
import Article from "Views/article"

import Popup from './Components/Layout/Popup/popup'

import Visualisation from "Views/visualisation";
import Retouches from "Views/Service/retouches"

// import { createBrowserHistory } from 'history';
// import { createBrowserHistory } from "history";
// import createHistory from 'history/createBrowserHistory'

import IMGFichier from './Assets/img/accueil/machine-photo-mannequin-invisible.jpg'

// history.listen((location) => {
//     console.log("test")
//     console.log(window.location.pathname + window.location.search)
//     // ReactGA.pageview(window.location.pathname + window.location.search);
// });

const App = () => {
    // const [windowWidth, windowHeight] = useWindowSize(); //Force le rendu au redimensionnement de la fenÃªtre
    const [pageLoad, setPageLoad] = useState(false)
    const CookieBanner = useRef()
    const [backgroundBlack, setBackgroundBlack] = useState(false)

    const [cookieAccept, setCookieAccept] = useState()

    let popup;

    // history.listen(location => {

    //     console.log('COUCOU')
    //     // ReactGA.set({ page: location.pathname });
    //     // ReactGA.pageview(location.pathname);
    // });
    // useEffect(() => {

    //     console.log('COUCOU')
    //     //  ReactGA.pageview(window.location.pathname + window.location.search)
    //  }, [history])

    // console.log(history)

    useEffect(() => {

        if(localStorage.getItem("cookieBanner2")){
            CookieBanner.current.style.display = "none"
        }

        if(localStorage.getItem("cookieBannerAccept")){
            setCookieAccept(true)
        }
    }, [])

    // console.log(history)

    const cookieOK = () => {

        localStorage.setItem("cookieBanner2", true);
        localStorage.setItem("cookieBannerAccept", true);
        setCookieAccept(true)
        CookieBanner.current.style.display = "none"

    }

    const cookieOFF = () => {
        localStorage.setItem("cookieBanner2", true);
        CookieBanner.current.style.display = "none"
    }

    /*useEffect(() => {
        if(localStorage.getItem("CookieNewsletterBanner")){
            popupRef.style.display = "none";
        }
    });*/

    if(localStorage.getItem("CookieNewsletterBanner") !== 'true'){
        popup = <Popup />
    }

    return(
        <>

        <Helmet defer={false}>
            <meta property="og:title" content="E-Do Studio - Production express de photos et vidÃ©os premiums" />
            <meta property="og:description" content="Packshot horizontal, packshot vertical, packshot accessoires, packshot portÃ©. Studio photo grand angle, cyclorama. Production de photos et videos en haute  rÃ©solution." />
            <meta property="og:image" content={`https://e-do.studio${IMGFichier}`} />

            // VÃ©rification de domaine Facebook
            <meta name="facebook-domain-verification" content="ovvq9e122a8v6ye9kxzc1wy24oy911" />

            {/* <link rel="canonical" href="http://mysite.com/example" /> */}
        </Helmet>


        <ViewportProvider>
            {/* basename={'/'} */}
            <BrowserRouter> 
                <main>
                    <Switch>
                        <Route path="/landing" render={() => <Landing setPageLoad={setPageLoad} />} />
                        <div>
                            <Navbar pageLoad={pageLoad} backgroundBlack={backgroundBlack} cookieAccept={cookieAccept} />
                            <Route path="/" exact render={() => <Index setPageLoad={setPageLoad} setBackgroundBlack={setBackgroundBlack} />} />
                            <Route path="/menu-services" exact render={() => <ServicesMenu setPageLoad={setPageLoad} />} />

                            <Route path="/service-mise-en-scene-live" exact render={() => <Service setPageLoad={setPageLoad} key={Date.now()} />} />
                            <Route path="/service-packshot-horizontal" exact render={() => <Service setPageLoad={setPageLoad} key={Date.now()} />} />
                            <Route path="/service-mannequin-vertical" exact render={() => <Service setPageLoad={setPageLoad} key={Date.now()} />} />
                            <Route path="/service-accessoires-eclipse" exact render={() => <Service setPageLoad={setPageLoad} key={Date.now()} />} />
                            <Route path="/service-production-libre-cyclorama" exact render={() => <Cyclorama setPageLoad={setPageLoad} key={Date.now()} />} />
                            <Route path="/service-retouches" exact render={() => <Retouches setPageLoad={setPageLoad} key={Date.now()} />} />

                            <Route path="/galerie360" exact render={() => <Galerie360 setPageLoad={setPageLoad} />} />
