import { useTexture } from "@react-three/drei";
import { memo, useRef, useState } from "react";
import { PlaneGeometry, Mesh, MeshBasicMaterial } from "three";
import { useThree, useFrame } from "@react-three/fiber";
import { appliedForces } from "./helpers";
import { Photo } from "./Photo";

export const Photos = () => {
  const images = [
    "./test.jpg",
    "./img/2023_BOTTLES_BOOK2102 copie.webp",
    "./img/ATTIRE_THE_STUDIO_MBACH_ATTIRE_010_FRANKLIN_BLACK_028 copie.webp",
    "./img/GIAMBATTISTA_23FWPVCA5091-09VIS-6750_26 copie.webp",
    "./img/GIAMBATTISTA_A2TIDY-TA07-08 copie.webp",
    "./img/JPG_23-12-U-PA061B-J033_Front copie.webp",
    "./img/JPG_P220613151038_Fullbody_jpg_23 copie.webp",
    "./img/MELISSA_JPG_F-CS002-X033-22-side-tiff-1 copie.webp",
    "./img/Parfum_x_Y_Project-back-tiff-1 copie.webp",
    "./img/SHANGXIA_FR1223S007QUARTZ-Fullbody-tiff-1 copie.webp",
  ];

  const [photos, updatePhotos] = useState(
    images.map((image, index) => ({
      x: Math.random() * 10 - 5,
      y: Math.random() * 10 - 5,
      image,
      index,
    }))
  );

  const { viewport } = useThree();

  useFrame(({ mouse }) => {
    const updatedPhotos = photos.map((photo) => {
      const direction = photo.x > (viewport.width * mouse.x) / 2 ? -1 : 1;
      const newPosition = appliedForces(photo.index, direction);
      return {
        ...photo,
        x: photo.x + newPosition.x,
        y: photo.y + newPosition.y,
      };
    });
    updatePhotos(updatedPhotos);
  });

  return (
    <>
      {photos.map((photo) => (
        <Photo key={photo.index} x={photo.x} y={photo.y} image={photo.image} />
      ))}
    </>
  );
};
