import React, { useCallback, useEffect, useRef, useState } from "react";
import { Link } from "react-router-dom";
import { useMediaQuery } from "@react-hook/media-query";
import { Waypoint } from "react-waypoint";
import ScrollContainer from "react-indiana-drag-scroll";
import { Helmet } from "react-helmet";
import Lottie from "lottie-react";

import { useLocation } from "react-router-dom";

import anime from "animejs/lib/anime.es.js";

import "./galerie.scss";

import Footer from "../Components/Layout/Footer/footer";

import boutonSliderBlanc from "../Assets/animations/boutonMenuServices.json";

import { useTranslation } from "react-i18next";

import GalerieMenu from "./GalerieMenu";
import IMGPC from "./IMGPC";
import IMGMobile from "./IMGMobile";

import GalerieEclipse from "./galerie-eclipse";
import GalerieLive from "./galerie-live";
import GalerieVertical from "./galerie-live";
import GalerieHorizontal from "./galerie-live";

const Galerie = ({ setPageLoad, setSelectedLink }) => {
  const matches = useMediaQuery("only screen and (min-width: 1200px)");
  const location = useLocation();
  const { selectedLink = "all" } = location.state || {};
  const [scrollX, setScrollX] = useState(0);

  const handleScroll = (event) => {
    setScrollX(event);
  };

  const PMS_BoutonPCNextButton = useRef();
  const PMS_BoutonPCPrecButton = useRef();

  const sliderNavSuiv = () => {
    PMS_BoutonPCNextButton.current.play();
    setTimeout(() => {
      PMS_BoutonPCNextButton.current.stop();
    }, 600);
  };

  const sliderNavPrec = () => {
    PMS_BoutonPCPrecButton.current.play();
    setTimeout(() => {
      PMS_BoutonPCPrecButton.current.stop();
    }, 600);
  };

  function scrollLeft() {
    const scrollBox = document.getElementsByClassName("galeriePCWrapper")[0];
    sliderNavPrec();
    scrollBox.scrollBy({
      left: -500,
      behavior: "smooth",
    });
  }

  function scrollRight() {
    const scrollBox = document.getElementsByClassName("galeriePCWrapper")[0];
    sliderNavSuiv();
    scrollBox.scrollBy({
      left: 500,
      behavior: "smooth",
    });
  }

  useEffect(() => {
    const scrollBox = document.getElementsByClassName("galeriePCWrapper")[0];

    const keyScroll = (e) => {
      const key = e.keyCode;
      if (key == "39") {
        scrollBox.scrollBy({
          left: 500,
          behavior: "smooth",
        });
      } else if (key == "37") {
        scrollBox.scrollBy({
          left: -500,
          behavior: "smooth",
        });
      }
    };

    document.addEventListener("keydown", keyScroll);
  }, []);

  useEffect(() => {
    anime({
      targets: ".PMS_BoutonPCNextButton",
      opacity: [0, 1],
      easing: "easeInOutSine",
      duration: 500,
      delay: 300,
    });

    anime({
      targets: ".PMS_BoutonPCPrecButton",
      opacity: [0, 1],
      easing: "easeInOutSine",
      duration: 500,
      delay: 300,
    });

    //Animation du bouton
  }, []);

  const videoRef = useRef(null);

  const [imgHover, setImgHover] = useState(false);

  const handleHover = (event) => {
    videoRef.current.style.opacity = 0.8;
    videoRef.current.style.filter = "grayscale(1)";
  };

  const handleMouseOut = (event) => {
    videoRef.current.style.opacity = 1;
    videoRef.current.style.filter = "grayscale(0)";
  };

  console.log(selectedLink);

  return (
    <>
      <GalerieMenu
        setPageLoad={setPageLoad}
        selectedLink={selectedLink}
        setSelectedLink={setSelectedLink}
      />
      {matches ? (
        <div
          className="galeriePC"
          style={{ cursor: "url(cursor/cursor.svg), auto" }}
        >
          {/* <div className="brand">{imgHover}</div> */}
          <ScrollContainer
            className="galeriePCWrapper"
            onScroll={handleScroll}
            hideScrollbars={false}
          >
            {selectedLink === "all" && (
              <>
                <IMGPC
                  linkUrl="/service-packshot-horizontal"
                  src="black_tee_staff.png"
                  lar="28"
                  haut="25"
                  ajustHauteurTop="6"
                  ajustHauteurBottom=""
                  anim={2}
                  scrollX={scrollX}
                  marque="Staff"
                  alt="black tee staff"
                />
                <IMGPC
                  linkUrl="/service-packshot-horizontal"
                  src="inoui_accesoires.png"
                  lar="25"
                  haut="35"
                  ajustHauteurTop=""
                  ajustHauteurBottom=""
                  anim={1}
                  scrollX={scrollX}
                  marque="Inoui"
                  alt="inoui accesoires"
                />
                <IMGPC
                  linkUrl="/service-packshot-horizontal"
                  src="cargo_mouty_details.jpg"
                  lar="22"
                  haut="27"
                  ajustHauteurTop=""
                  ajustHauteurBottom="2"
                  anim={1}
                  scrollX={scrollX}
                  marque="Mouty"
                  alt="cargo mouty details"
                />
                <IMGPC
                  linkUrl="/service-packshot-horizontal"
                  src="hast_pants_corduroy.jpg"
                  lar="22"
                  haut="23"
                  ajustHauteurTop=""
                  ajustHauteurBottom="5"
                  anim={1}
                  scrollX={scrollX}
                  marque="Hast"
                  alt="hast pants corduroy"
                />
                <IMGPC
                  linkUrl="/service-packshot-horizontal"
                  src="Dentro_bag_1.jpg"
                  lar="22"
                  haut="32"
                  ajustHauteurTop=""
                  ajustHauteurBottom=""
                  anim={2}
                  scrollX={scrollX}
                  marque="Dentro"
                  alt="Dentro bag"
                />
                <IMGPC
                  linkUrl="/service-packshot-horizontal"
                  src="chemise_kid_super.png"
                  lar="32"
                  haut="28"
                  ajustHauteurTop=""
                  ajustHauteurBottom=""
                  anim={1}
                  scrollX={scrollX}
                  marque="Kid Super"
                  alt="chemise kid super"
                />
                <IMGPC
                  linkUrl="/service-packshot-horizontal"
                  src="hast_gants.jpg"
                  lar="28"
                  haut="27"
                  ajustHauteurTop=""
                  ajustHauteurBottom="5"
                  anim={1}
                  scrollX={scrollX}
                  marque="Hast"
                  alt="hast gants"
                />
                <IMGPC
                  linkUrl="/service-packshot-horizontal"
                  src="classic_motors_leather_red_jacket.jpg"
                  lar="32"
                  haut="29"
                  ajustHauteurTop=""
                  ajustHauteurBottom="3"
                  anim={1}
                  scrollX={scrollX}
                  marque="Classic motors"
                  alt="classic motors leather red jacket"
                />
                <IMGPC
                  linkUrl="/service-packshot-horizontal"
                  src="inoui_editions_scarf.png"
                  lar="28"
                  haut="18"
                  ajustHauteurTop=""
                  ajustHauteurBottom=""
                  anim={1}
                  scrollX={scrollX}
                  marque="Inoui"
                  alt="inoui editions scarf"
                />
                <IMGPC
                  linkUrl="/service-packshot-horizontal"
                  src="inoui_editions_tote_bag.png"
                  lar="25"
                  haut="34"
                  ajustHauteurTop=""
                  ajustHauteurBottom=""
                  anim={1}
                  scrollX={scrollX}
                  marque="Inoui"
                  alt="inoui editions tote bag"
                />
                <IMGPC
                  linkUrl="/service-packshot-horizontal"
                  src="hast_chemise_bleu.jpg"
                  lar="32"
                  haut="30"
                  ajustHauteurTop="7"
                  ajustHauteurBottom=""
                  anim={1}
                  scrollX={scrollX}
                  marque="Hast"
                  alt="hast chemise bleu"
                />
                <IMGPC
                  linkUrl="/service-packshot-horizontal"
                  src="kid_super_foulards.png"
                  lar="34"
                  haut="35"
                  ajustHauteurTop=""
                  ajustHauteurBottom="5"
                  anim={1}
                  scrollX={scrollX}
                  marque="Kid Super"
                  alt="kid super foulards"
                />
                <IMGPC
                  linkUrl="/service-packshot-horizontal"
                  src="cargo_mouty_green.jpg"
                  lar="25"
                  haut="27"
                  ajustHauteurTop=""
                  ajustHauteurBottom="8"
                  anim={1}
                  scrollX={scrollX}
                  marque="Mouty"
                  alt="cargo mouty green"
                />
              </>
            )}
          </ScrollContainer>
        </div>
      ) : (
        <div>Test</div>
      )}
    </>
  );
};

export default Galerie;
